tr_replace("-","",$the_carousel_name);\r\n					echo "<script type=\\"text/javascript\\">\r\n						function ".$no_dashes_the_carousel_name."_initCallback(carousel){\r\n							// Disable autoscrolling if the user clicks the prev or next button.\r\n							carousel.buttonNext.bind(''click'', function() {\r\n								carousel.startAuto(0);\r\n							});\r\n						\r\n							carousel.buttonPrev.bind(''click'', function() {\r\n								carousel.startAuto(0);\r\n							});\r\n						\r\n							// Pause autoscrolling if the user moves with the cursor over the clip.\r\n							carousel.clip.hover(function() {\r\n								carousel.stopAuto();\r\n							}, function() {\r\n								carousel.startAuto();\r\n							});\r\n						}\r\n						\r\n						jQuery(document).ready(function() {\r\n							jQuery(''#".$the_carousel_name."-portfolio'').jcarousel({\r\n								auto: ".getGlobalVars($properties,''work_jcarousel_auto'').",\r\n								wrap: ''".getGlobalVars($properties,''work_jcarousel_wrap'')."'',\r\n								scroll: ".getGlobalVars($properties,''work_jcarousel_scrollamt'').",\r\n								initCallback: ".$no_dashes_the_carousel_name."_initCallback\r\n							});\r\n						});\r\n						</script>\r\n					";\r\n					\r\n					/* GET SKINID */\r\n					$GET_SKIN_ID=mysql_query("SELECT * FROM {$properties->DB_PREFIX}jc_themes WHERE id=''".getGlobalVars($properties,"work_rotator_theme")."''");\r\n					if(mysql_num_rows($GET_SKIN_ID)<1){\r\n						/* NOT THERE */\r\n					} else {\r\n						while($FETCH_SKIN_ID=mysql_fetch_array($GET_SKIN_ID)){\r\n							$skinIDname=$FETCH_SKIN_ID[''name''];\r\n						}\r\n					}echo "\r\n					<div class=\\"w-p-c-row\\">\r\n						<div class=\\"w-p-c-row-title\\">".$name." Portfolio</div>\r\n						<ul id=\\"".$the_carousel_name."-portfolio\\" class=\\"jcarousel-skin-".$skinIDname."\\">\r\n								";						\r\n								/* BUILD THE LIST OF ITEMS FROM PROJECTS DB */\r\n								$GET_PROJECTS=mysql_query("SELECT * FROM {$properties->DB_PREFIX}work_projects WHERE portfolio_id=''