(29, 'off', 'work-projects', 'single', 'Projects', 'nat4an current projects,projects by Nat4an,projects,work', 'padmain', 1, '', '2012-07-26', '', 'if(isset($_GET[''meta''])){\r\n	switch($_GET[''meta'']){		\r\n		case ''normal'':\r\n			/* NORMAL META */\r\n			@$credentials=$_GET[''projectname''].":::";\r\n			build_portfolio_page(''normal'',$properties,$credentials,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		default:\r\n			/* meta is not being used, do normal page */\r\n			@$credentials="";\r\n			build_portfolio_page(''normal'',$properties,$credentials,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n	}\r\n} else {\r\n	@$credentials="";\r\n	build_portfolio_page(''normal'',$properties,$credentials,$NEW_PATH_TO_THEME_ASSETS);\r\n}\r\n\r\nfunction build_portfolio_page($operand,$properties,$credentials,$NEW_PATH_TO_THEME_ASSETS){\r\n	if($_SERVER[''HTTP_HOST'']=="localhost"){$WEBSITE_URL=$properties->WEBSITE_TEST_URL;}else{$WEBSITE_URL=$properties->WEBSITE_REMO_URL;}\r\n	@$launchpad=$_GET[''launchpad''];\r\n	@$page=$_GET[''page''];\r\n	switch($operand){		\r\n		case ''normal'':\r\n		$credentials_list=explode(":::",$credentials);\r\n		$projectname=$credentials_list[0];\r\n		\r\n		@$full_name=str_replace("-"," ",$projectname);\r\n		\r\n		$GET_PROJECTS=mysql_query("SELECT * FROM {$properties->DB_PREFIX}work_projects WHERE title=''$full_name''");\r\n		if(mysql_num_rows($GET_PROJECTS)<1){\r\n			\r\n		} else {\r\n			$FETCH_PROJECTS=mysql_fetch_array($GET_PROJECTS);\r\n				$real_name=$FETCH_PROJECTS[''title''];\r\n				$filename=$FETCH_PROJECTS[''filename''];\r\n				$installation=$FETCH_PROJECTS[''installation''];\r\n		}\r\n		\r\n		echo "<br />";\r\n		echo "<div class=\\"back-button\\" onclick=\\"window.location.href=''".$WEBSITE_URL.@$launchpad."/work''\\">&lt;</div>";\r\n		echo " <div class=\\"wyo-button\\">".$real_name."</div>";\r\n		echo "<br />";\r\n		\r\n		echo "<div id=\\"work-project-container\\">";\r\n			echo "<div class=\\"w-p-c-row\\">";\r\n				echo "<div class=\\"w-p-c-row-title\\">Preview</div>";\r\n				echo "<br />";\r\n				echo "<iframe src=\\"".$WEBSITE_URL."projects/".$projectname."\\" width=\\"100%\\" height=\\"1000\\" frameborder=\\"0\\" name=\\"n-view\\"></iframe>";\r\n				echo "<br />";\r\n				echo "<br />";\r\n			echo "</div>";\r\n			echo "<div class=\\"w-p-c-row\\">";\r\n				echo "<div class=\\"w-p-c-row-title\\">Download</div>";\r\n				echo "<br />";\r\n				echo "<a href=\\"".$WEBSITE_URL."projects/".$projectname."/downloads/".$filename."\\"><img src=\\"".$WEBSITE_URL.$NEW_PATH_TO_THEME_ASSETS."/images/File.png\\" width=\\"100\\" height=\\"100\\" style=\\"cursor: pointer;\\"></a>";\r\n				echo "<br />";\r\n				echo "<br />";\r\n			echo "</div>";\r\n			echo "<div class=\\"w-p-c-row\\">";\r\n				echo "<div class=\\"w-p-c-row-title\\">Installation</div>";\r\n				echo "<br />";\r\n				echo "<div class=\\"w-p-c-col-description\\">".$installation."</div>";\r\n				echo "<br />";\r\n			echo "</div>";\r\n		echo "</div>";\r\n		break;\r\n	}\r\n}', '', '', '', '', '', '', '', 'no', 'off', 'off');
