utton\\" id=\\"pagination-page-last\\" name=\\"pagination-page-last\\" class=\\"pagination-link-selected\\" value=\\"".$num_of_btns."\\" onclick=\\"pagination(''last'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/'','''',''".$CURRENT_PAGINATION."'',''".$num_of_btns."'');\\"> ";}else{echo "<input type=\\"button\\" id=\\"pagination-page-last\\" name=\\"pagination-page-last\\" class=\\"pagination-link\\" value=\\"".$num_of_btns."\\" onclick=\\"pagination(''last'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/'','''',''".$CURRENT_PAGINATION."'',''".$num_of_btns."'');\\"> ";}\r\n						\r\n						//forward arrow\r\n						if($pagination == $num_of_btns){echo "<input type=\\"button\\" id=\\"pagination-page-forward\\" name=\\"pagination-page-forward\\" disabled=\\"disabled\\" class=\\"pagination-link\\" value=\\"&gt;\\" onclick=\\"pagination(''forward'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/'','''',''".$CURRENT_PAGINATION."'','''');\\">";}else{echo "<input type=\\"button\\" id=\\"pagination-page-forward\\" name=\\"pagination-page-forward\\" class=\\"pagination-link\\" value=\\"&gt;\\" onclick=\\"pagination(''forward'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/'','''',''".$CURRENT_PAGINATION."'','''');\\">";}\r\n						echo "</form>";\r\n					echo "</div>";\r\n				}\r\n				\r\n				/* END BUILD PAGINATION */					\r\n					\r\n				$FETCH_USER_ID=mysql_fetch_array($FIND_USER_ID);\r\n				$userID=$FETCH_USER_ID[''id''];\r\n				$limit_string=$pname_entries_limit;	\r\n				\r\n				//get all \r\n				$GET_PNAME_ALLENTRIES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_entries WHERE director=''$id'' AND status=''".$PNAME_PUBLISHED_NAME."''") or die(''uh oh! ''.mysql_error());\r\n				$FETCH_PNAME_ALLENTERIES=mysql_fetch_array($GET_PNAME_ALLENTRIES);\r\n				if($FETCH_PNAME_ALLENTERIES[''dateandtime'']!=""){\r\n					if(mysql_num_rows($GET_ALLPNAME_ENTRIES)<1){\r\n						$THE_QUERY="SELECT * FROM {$properties->DB_PREFIX