ERE id=''$staff_type''");\r\n							$FETCH_STAFF_TYPE=mysql_fetch_array($GET_STAFF_CODE);\r\n							$staff_title=$FETCH_STAFF_TYPE[''name''];\r\n							echo "<option value=\\"".$FETCH_STAFF[''id'']."\\">".$FETCH_STAFF[''fname'']." ".$FETCH_STAFF[''lname'']." (".$staff_title.")"."</option>";	\r\n						}\r\n					}				\r\n				echo "</select>\r\n			</div>\r\n		</div>\r\n		\r\n		<div class=\\"formLayoutTableRowSingle\\">\r\n			<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n				<label>Reason</label>\r\n			</div>\r\n			<div class=\\"formLayoutTableRowRightColSingle\\">\r\n				<select name=\\"contact_reason\\" id=\\"contact_reason\\">";\r\n					$GET_REASONS=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$DBTBLSTART."".$QUERY_TABLENAME.$QUERY_TABLESUBNAME."_reasons ORDER BY reason") or die(''uh oh! ''.mysql_error());\r\n					if(mysql_num_rows($GET_REASONS)<1){\r\n						echo "<option value=\\"na\\">---- no reasons avail ----</option>";\r\n					} else {\r\n						echo "<option value=\\"na\\">---- choose a reason ----</option>";	\r\n						while($FETCH_REASONS=mysql_fetch_array($GET_REASONS)){\r\n							echo "<option value=\\"".$FETCH_REASONS[''rcode'']."\\">".$FETCH_REASONS[''reason'']."</option>";	\r\n						}\r\n					}\r\n				echo "</select>\r\n			</div>\r\n		</div>";\r\n		\r\n		echo "<div class=\\"formLayoutTableRowSingle\\">\r\n			<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n				<label>Your Message</label>\r\n			</div>\r\n			<div class=\\"formLayoutTableRowRightColSingle\\">\r\n				<textarea cols=\\"50\\" rows=\\"10\\" id=\\"contact_message\\" name=\\"contact_message\\"></textarea>\r\n			</div>\r\n		</div>\r\n		\r\n		<div class=\\"formLayoutTableRowSingle\\">\r\n			<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n				\r\n			</div>\r\n			<div class=\\"formLayoutTableRowRightColSingle\\">\r\n				<input type=\\"submit\\" name=\\"contact_send\\" id=\\"contact_send\\" value=\\"Send\\" class=\\"submit\\" /> <input type=\\"reset\\" name=\\"contact_reset\\" id=\\"contact_reset\\" value=\\"Start Over\\" class=\\"submit\\" />\r\n			</div>\r\n		</div>\r\n		\r\n		<div class=\\"formLayoutTableRowSingle\\">\r\n			<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n				\r\n			</div>\r\n			<div class=\\"formLayoutTableRowRightColSingle\\">\r\n				\r\n\r\n			</div>\r\n		</div>\r\n		\r\n		<div class=\\"formLayoutTableRowSingle\\">\r\n			<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n				Options\r\n			</div>\r\n			<div class=\\"formLayoutTableRowRightColSingle\\">\r\n				";\r\n				echo "\r\n				Already have an open ticket/issue?\r\n			</div>\r\n		</div>\r\n		\r\n		<div class=\\"formLayoutTableRowSingle\\">\r\n			<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n				\r\n			</div>\r\n			<div class=\\"formLayoutTableRowRightColSingle\\">\r\n				Check the status of it @ <a href=\\"".$WEBSITE_URL.$PADINFO."/".$FORMNAME."/checkstatus\\" class=\\"white-url\\">".$FORMNAME."/checkstatus</a>\r\n\r\n			</div>\r\n		</div>\r\n	</div>\r\n	</form>";\r\n}\r\n}', '', '', '$GET_MODULES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}pages_modules WHERE page=''$page'' AND sidebar=''1'' AND launchpad=''$launchpadPN'' AND status=''Published'' AND toggle_visible=''on'' ORDER BY arr") or die(''uh oh! ''.mysql_error());\r\nwhile($FETCH_MODULES=mysql_fetch_array($GET_MODULES)){\r\n	$title=$FETCH_MODULES[''title''];\r\n	$contents=$FETCH_MODULES[''contents''];\r\n	$toggle_title=$FETCH_MODULES[''toggle_title''];\r\n	$toggle_visible=$FETCH_MODULES[''toggle_visible''];\r\n	if($toggle_title == "off"){/* no title */} else if($toggle_title == "on"){echo "<h2 class=\\"pages-sidebar-title\\">{$title}</h2>";}\r\n	echo "<div class=\\"pages-sidebar-contents\\">";\r\n		//convert contents\r\n		$contents=converter($properties,$contents,''basic'',''to'');\r\n		if($toggle_visible == "on"){eval($contents);}else if($toggle_visible == "off"){/* not visible */}\r\n	echo "</div>";\r\n}', '', '', '', '', 'yes', 'off', 'Published', 7, '127.0.0.1'),
(4, 'off', 'home', 'double', 'Home', 'Nat4an,Nathan,Home,web designer,', 'padmain', 1, '', '2012-09-08', '', '$GET_MODULES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}pages_modules WHERE page=''$properties->PADMAIN$page'' AND type=''maincontent'' AND launchpad=''$launchpadPN'' AND toggle_visible=''on'' AND status=''Published'' ORDER BY arr") or die(''uh oh! ''.mysql_error());\r\nwhile($FETCH_MODULES=mysql_fetch_array($GET_MODULES)){\r\n	$title=$FETCH_MODULES[''title''];\r\n        $mini_title=$FETCH_MODULES[''mini_title''];\r\n	$contents=$FETCH_MODULES[''contents''];\r\n	$toggle_title=$FETCH_MODULES[''toggle_title''];\r\n	if($toggle_title == "off"){/* no title */} else if($toggle_title == "on"){echo "<div class=\\"pages-sidebar-title\\">{$title} <div class=\\"pages-sidebar-minititle\\">{$mini_title}</div></div>";}\r\n	echo "<div class=\\"pages-maincontent-contents\\">";\r\n		//convert contents\r\n		$contents=converter($properties,$contents,''basic'',''to'');\r\n		eval($contents);\r\n	echo "</div>";\r\n}', '', '', '$GET_MODULES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}pages_modules WHERE page=''$properties->PADMAIN$page'' AND type=''sidebar'' AND sidebar=''1'' AND launchpad=''$launchpadPN'' AND toggle_visible=''on'' AND status=''Published'' ORDER BY arr") or die(''uh oh! ''.mysql_error());\r\nwhile($FETCH_MODULES=mysql_fetch_array($GET_MODULES)){\r\n	$title=$FETCH_MODULES[''title''];\r\n	$contents=$FETCH_MODULES[''contents''];\r\n	$toggle_title=$FETCH_MODULES[''toggle_title''];\r\n	if($toggle_title == "off"){/* no title */} else if($toggle_title == "on"){echo "<div class=\\"pages-sidebar-title\\">{$title}</div>";}\r\n	echo "<div class=\\"pages-sidebar-contents\\">";\r\n		//convert contents\r\n		$contents=converter($properties,$contents,''basic'',''to'');\r\n		eval($contents);\r\n	echo "</div>";\r\n}', '', '', '$GET_MODULES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}pages_modules WHERE page=''$properties->PADMAIN$page'' AND type=''sidebar'' AN