(5, 'off', 'search', 'double', 'Search', 'nat4an current projects,projects by Nat4an,projects,work', 'padmain', 1, '', '2012-09-10', '', '/* SEARCH FUNCTION FOR NAT4AN.COM */\r\n/* EVERYTHING YOU NEED TO SEARCH MY SITE IS RIGHT HERE IN THE CODE AND IN THE PROPS.PHP PAGE */\r\n/* COPYRIGHT ÃÂ© 2012 NAT4AN CORP. NOT TO BE COPIED */\r\n/* ORIGINAL CODING WORK: NATHAN SMYTH */\r\n/* --------------------------------------------------------- BEGIN ---------------------------------------------------------*/\r\nerror_reporting(0);\r\n//get variables and other materials\r\nmb_language(''uni'');\r\nmb_internal_encoding(''UTF-8'');\r\n@$searchQuery=$_POST[''search''];\r\n@$SEARCH_TEXT=$properties->SEARCH_TEXT;\r\nif($searchQuery == $SEARCH_TEXT){$SEARCH_TEXT="&quot;".$SEARCH_TEXT."&quot;";}else if(strlen(trim(preg_replace(''/\\xc2\\xa0/'','' '',$searchQuery))) == 0){$SEARCH_TEXT="nothing";}\r\n\r\n//build search\r\nif(($searchQuery == "") || ($searchQuery == " ") || (strlen(trim(preg_replace(''/\\xc2\\xa0/'','' '',$searchQuery))) == 0)){\r\n	echo "<h2 class=\\"pages-maincontent-title\\">Uh oh! It looks like a diglett just appeared here</h2>";\r\n	echo "<h2>You must type in something to search for it. You cannot just search for {$SEARCH_TEXT}.</h2>";\r\n} else {\r\n	echo "<h2 class=\\"pages-maincontent-title\\">Searching Nat4an.com for &quot;".$searchQuery."&quot;</h2>";\r\n	SEARCH($properties,$searchQuery);\r\n}', '', '<br />', '$GET_MODULES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}pages_modules WHERE page=''$page'' AND sidebar=''1'' AND launchpad=''$launchpadPN'' ORDER BY arr") or die(''uh oh! ''.mysql_error());\r\nwhile($FETCH_MODULES=mysql_fetch_array($GET_MODULES)){\r\n	$title=$FETCH_MODULES[''title''];\r\n	$contents=$FETCH_MODULES[''contents''];\r\n	$toggle_title=$FETCH_MODULES[''toggle_title''];\r\n	$toggle_visible=$FETCH_MODULES[''toggle_visible''];\r\n	if($toggle_title == "off"){/* no title */} else if($toggle_title == "on"){echo "<h2 class=\\"pages-sidebar-title\\">{$title}</h2>";}\r\n	echo "<div class=\\"pages-sidebar-contents\\">";\r\n		//convert contents\r\n		$contents=converter($properties,$contents,''basic'',''to'');\r\n		if($toggle_visible == "on"){eval($contents);}else if($toggle_visible == "off"){/* not visible */}\r\n	echo "</div>";\r\n}', '', '', '', '', 'no', 'off', 'Published', 0, '');
