(12, 'restrict all', 'contact', 'double', 'Contact', 'contact Nat4an,contact Nathan Smyth,contacting Nat4an Corp,contact the musik maker', 'pad3', 4, '', '2012-09-19', '', '$TITLE                    = "Contact The Musik Maker";\r\n$PADINFO                  = $properties->PAD3;\r\n$DBTBLSTART               = "";\r\n$EXTRA_NAME               = "The Musik Maker on "; //for the email subject\r\n$EXTRA_NAME_SAFE          = "_tmm"; //the safe version of $EXTRA_NAME\r\nglobal $FORMNAME;\r\n$FORMNAME                 = "contact"; //usually the page name\r\n$POC_DB                   = "tmm_virtuosos"; //could be: users, tmm_virtuosos, etc\r\n$POC_DB_WHERE_TYPE_CLAUSE = "type != ''listener''"; //the where clause of the poc db\r\n$POC_DB_ORDER_BY          = "vname";\r\n$QUERY_TABLENAME	  = "queries_"; //the part after the prefix of the tablename\r\n$QUERY_TABLESUBNAME	  = "tmm"; //the part after the _ after the $QUERY_TABLENAME\r\n\r\n/* ---------- DO NOT EDIT BELOW THIS LINE OR ELSE YOU WILL BREAK IT --------- */\r\n\r\nif($_SERVER[''HTTP_HOST'']=="localhost"){$WEBSITE_URL=$properties->WEBSITE_TEST_URL;}else{$WEBSITE_URL=$properties->WEBSITE_REMO_URL;}\r\nif(isset($_GET[''meta''])){\r\nswitch($_GET[''meta'']){\r\n	case ''checkstatus'':\r\n		@$ticket_id="";\r\n		echo "<br />";\r\n		echo "<div class=\\"back-button\\" onclick=\\"window.location.href=''".@$WEBSITE_URL.$PADINFO."/".@$page."''\\">&lt;</div>";\r\n		if($ticket_id == ""){echo " <div class=\\"wyo-button\\">Check Status of a Ticket</div>";}else{$ticket_id=$_GET[''ticketid''];echo " <div class=\\"wyo-button\\">Check Status of Ticket ID: <b>".substr($ticket_id,0,40)."<a title=\\"{$ticketid}\\" class=\\"white-url\\">...</a></b></div>";}\r\n		echo "<br />";\r\n		echo "<br />";\r\n		if((isset($_GET[''ticketid''])) || (@$_POST[''ticket_id_check''])){\r\n			if(isset($_GET[''ticketid''])){$ticket_id=$_GET[''ticketid''];}else if(isset($_POST[''ticket_id_check''])) {if($_POST[''contact_yemail''] != "Enter Your Email"){/* LOOKUP BY EMAIL */$GET_TICKET_BY_EMAIL=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$DBTBLSTART."".$QUERY_TABLENAME.$QUERY_TABLESUBNAME." WHERE contact_email=''".$_POST[''contact_yemail'']."'' ORDER BY dateandtime DESC") or die(''uh oh! ''.mysql_error());if(mysql_num_rows($GET_TICKET_BY_EMAIL)<1){echo "<div id=\\"formLayoutTableSingle\\"><div class=\\"formLayoutTableRowSingle\\"><div class=\\"formLayoutTableRowLeftColSingle\\">Response</div><div class=\\"formLayoutTableRowRightColSingle\\">Sorry I could not find a Ticket ID under that email!<br /><a onclick=\\"history.go(-1)\\" class=\\"white-url\\">Go Back</a></div></div></div><br />";} else {while($FETCH_TICKET_BY_EMAIL=mysql_fetch_array($GET_TICKET_BY_EMAIL)){$ticket_id.=$FETCH_TICKET_BY_EMAIL[''ticket_id'']."::::::";}}} else {$ticket_id=$_POST[''ticket_id''];}}\r\n			\r\n			if((isset($_POST[''contact_yemail''])) && ($_POST[''contact_yemail''] != "Enter Your Email")){\r\n				/* check by email */\r\n				//explode the ticket_id\r\n				@$ticket_id_list="";\r\n				$ticket_id_list=explode("::::::",$ticket_id);\r\n				for($i=0; $i<count($ticket_id_list)-1; $i++){\r\n					$GET_TICKET=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$DBTBLSTART."".$QUERY_TABLENAME.$QUERY_TABLESUBNAME." WHERE ticket_id=''$ticket_id_list[$i]'' ORDER BY dateandtime DESC") or die(''uh oh! ''.mysql_error());\r\n					if(mysql_num_rows($GET_TICKET)<1){\r\n					echo "<div id=\\"formLayoutTableSingle\\">\r\n						<div class=\\"formLayoutTableRowSingle\\">\r\n							<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n								Response\r\n							</div>\r\n							<div class=\\"formLayoutTableRowRightColSingle\\">\r\n								Sorry I could not find your Ticket ID! <a onclick=\\"history.go(-1)\\" class=\\"white-url\\">Go Back</a>\r\n							</div>\r\n						</div>\r\n					</div>";\r\n					} else {\r\n						$FETCH_TICKET=mysql_fetch_array($GET_TICKET);\r\n						@$contact_name		= $FETCH_TICKET[''contact_name''];\r\n						@$contact_email		= $FETCH_TICKET[''contact_email''];\r\n						@$poc				= $FETCH_TICKET[''poc''];\r\n						@$reason			= $FETCH_TICKET[''reason''];\r\n						@$contact_message	= $FETCH_TICKET[''contact_message''];\r\n						@$extra_notes		= $FETCH_TICKET[''extra_notes''];\r\n						@$status			= $FETCH_TICKET[''status''];\r\n						\r\n						echo "<div id=\\"formLayoutTableSingle\\">\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Ticket ID\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									<b>{$ticket_id_list[$i]}</b>\r\n								</div>\r\n							</div>\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Contact Name\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									{$contact_name}\r\n								</div>\r\n							</div>\r\n\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Contact Email\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									{$contact_email}\r\n								</div>\r\n							</div>\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Point of Contact\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									";\r\n									$GET_POC=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$POC_DB." WHERE ".$POC_DB_WHERE_TYPE_CLAUSE." AND id=''$poc'' AND isIncludedInMTS=''yes''") or die(''uh oh! ''.mysql_error());\r\n									$FETCH_POC=mysql_fetch_array($GET_POC);\r\n									//get the staff title\r\n									@$staff_type=$FETCH_POC[''staff_type''];\r\n									@$GET_STAFF_CODE=mysql_query("SELECT * FROM {$properties->DB_PREFIX}staff_types WHERE id=''$staff_type''");\r\n									$FETCH_STAFF_TYPE=mysql_fetch_array($GET_STAFF_CODE);\r\n									$staff_title=$FETCH_STAFF_TYPE[''name''];\r\n									echo $FETCH_POC[''fname'']." ".$FETCH_POC[''lname''] . " (".$staff_title.")";\r\n									echo "\r\n								</div>\r\n							</div>\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Reason\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									";\r\n									$GET_REASON=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$DBTBLSTART."".$QUERY_TABLENAME.$QUERY_TABLESUBNAME."_reasons WHERE rcode=''$reason''") or die(''uh oh! ''.mysql_error());\r\n									$FETCH_REASON=mysql_fetch_array($GET_REASON);\r\n									echo $FETCH_REASON[''reason''];\r\n									echo "\r\n								</div>\r\n							</div>\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Message\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									{$contact_message}\r\n								</div>\r\n							</div>\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Extra Notes\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									{$extra_notes}\r\n								</div>\r\n							</div>\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Status\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									{$status}\r\n								</div>\r\n							</div>\r\n							\r\n							<div class=\\"formLayoutTableRowSingle\\">\r\n								<div class=\\"formLayoutTableRowLeftColSingle\\">\r\n									Directions\r\n								</div>\r\n								<div class=\\"formLayoutTableRowRightColSingle\\">\r\n									<div style=\\"color: blue;font-weight: bold;\\">Please exit <a href=\\"".$WEBSITE_URL.$PADINFO."/