(17, 'restrict non head admins', 'a-z-list-reviews', 'double', 'A to Z List Reviews', 'anime, a to z, anime names, titles of animes', 'pad1', 2, '', '2012-09-19', '<br />', '/* YOU MIGHT HAVE TO SEARCH FOR THE PAD NAME (PADMAIN, PAD1 THROUGH 4, AND CHANGE IT BASED ON WHAT PAD */\r\n$pname="pages_af_atoz";\r\n$pname_special="A to Z List Reviews";\r\n$pname_uri="a-z-list-reviews";\r\n$display_user=false;\r\n$PADINFO=$properties->PAD1;\r\n$PNAME_PUBLISHED_NAME="Published";\r\n/* ---------- DO NOT EDIT BELOW THIS LINE OR ELSE YOU WILL BREAK IT --------- */\r\nerror_reporting(0);\r\n//check for meta being used\r\nif(isset($_GET[''meta''])){\r\n	//find out what meta to use\r\n	switch($_GET[''meta'']){\r\n		/* ----- THE TEMPLATE IS ----- */\r\n		/* ------- INSTRUCTIONS ------ */\r\n		/* \r\n			Replace ''METANAME'' with\r\n			the name of the item to \r\n			be displayed.\r\n			\r\n			This is from the $_GET vars.\r\n		*/\r\n		/* ----- END INSTRUCTIONS ---- */\r\n		/*\r\n		case ''METANAME'':\r\n			$METANAME=$_GET[''METANAME''];\r\n			$credentials=$METANAME.":::";\r\n			build_pname_page(''METANAME'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user);\r\n		break;\r\n		*/\r\n		/* ----- END THE TEMPLATE----- */\r\n		\r\n		/* CUSTOM META OPTIONS GO HERE */\r\n		case ''category'':\r\n			$category=$_GET[''category''];\r\n			$credentials=$category.":::";\r\n			build_pname_page(''category'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''comment'':\r\n			$operand=$_GET[''operand''];\r\n			\r\n			switch($operand){\r\n				case ''checkstatus'':\r\n					$credentials=$operand.":::";\r\n					if(isset($_GET[''commentticketid''])){$credentials.=$_GET[''commentticketid''].":::";}\r\n					build_pname_page(''comment'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,