(30, 'off', 'blog', 'double', 'Blog', 'blog,Colleagues of Mine,Back on the Road,Creative Fotos,New Memories,The Library,What am I saying?', 'padmain', 1, '', '2012-07-26', '<br />', '/* YOU MIGHT HAVE TO SEARCH FOR THE PAD NAME (PADMAIN, PAD1 THROUGH 4, AND CHANGE IT BASED ON WHAT PAD */\r\n$pname="blog";\r\n$pname_special="Blog";\r\n$pname_uri="blog";\r\n$display_user=true;\r\n$PADINFO=$properties->PADMAIN;\r\n$PNAME_PUBLISHED_NAME="Published";\r\n/* ---------- DO NOT EDIT BELOW THIS LINE OR ELSE YOU WILL BREAK IT --------- */\r\nerror_reporting(0);\r\n//check for meta being used\r\nif(isset($_GET[''meta''])){\r\n	//find out what meta to use\r\n	switch($_GET[''meta'']){\r\n		/* ----- THE TEMPLATE IS ----- */\r\n		/* ------- INSTRUCTIONS ------ */\r\n		/* \r\n			Replace ''METANAME'' with\r\n			the name of the item to \r\n			be displayed.\r\n			\r\n			This is from the $_GET vars.\r\n		*/\r\n		/* ----- END INSTRUCTIONS ---- */\r\n		/*\r\n		case ''METANAME'':\r\n			$METANAME=$_GET[''METANAME''];\r\n			$credentials=$METANAME.":::";\r\n			build_pname_page(''METANAME'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user);\r\n		break;\r\n		*/\r\n		/* ----- END THE TEMPLATE----- */\r\n		\r\n		/* CUSTOM META OPTIONS GO HERE */\r\n		case ''category'':\r\n			$category=$_GET[''category''];\r\n			$credentials=$category.":::";\r\n			build_pname_page(''category'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''comment'':\r\n			$operand=$_GET[''operand''];\r\n			\r\n			switch($operand){\r\n				case ''checkstatus'':\r\n					$credentials=$operand.":::";\r\n					if(isset($_GET[''commentticketid''])){$credentials.=$_GET[''commentticketid''].":::";}\r\n					build_pname_page(''comment'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n				break;\r\n				default:\r\n					$credentials="";\r\n					build_pname_page(''normal'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n				break;\r\n			}\r\n		break;\r\n		\r\n		case ''botr'':\r\n			$credentials="";\r\n			$year=$_GET[''year''];\r\n			$month=$_GET[''month''];\r\n			$credentials=$year.":::".$month.":::";\r\n			build_pname_page(''botr'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''permalink'':\r\n			$credentials="";\r\n			$year=$_GET[''year''];\r\n			$month=$_GET[''month''];\r\n			$day=$_GET[''day''];\r\n			$title=$_GET[''title''];\r\n			$credentials=$year.":::".$month.":::".$day.":::".$title.":::";\r\n			build_pname_page(''permalink'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''tag'':\r\n			$tag=$_GET[''tag''];\r\n			$credentials=$tag.":::";\r\n			build_pname_page(''tag'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''users'':\r\n			$user=$_GET[''user''];\r\n			$credentials=$user.":::";\r\n			build_pname_page(''user'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''authors'':\r\n			$user=$_GET[''user''];\r\n			$credentials=$author.":::";\r\n			build_pname_page(''author'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''directors'':\r\n			$director=$_GET[''director''];\r\n			$credentials=$director.":::";\r\n			build_pname_page(''director'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''studios'':\r\n			$studio=$_GET[''studio''];\r\n			$credentials=$studio.":::";\r\n			build_pname_page(''studio'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		case ''networks'':\r\n			$network=$_GET[''network''];\r\n			$credentials=$network.":::";\r\n			build_pname_page(''network'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		\r\n		default:\r\n			$credentials="";\r\n			//meta is wrong or not there, do normal page\r\n			build_pname_page(''normal'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n		break;\r\n		/* END CUSTOM META OPTIONS GO HERE */\r\n	}\r\n} else {\r\n	//meta is not being used, do normal pname page\r\n    $credentials="";\r\n	build_pname_page(''normal'',$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS);\r\n}\r\n\r\nfunction build_pname_page($operation,$properties,$PADINFO,$page,$credentials,$wurl,$pname,$pname_special,$pname_uri,$display_user,$PNAME_PUBLISHED_NAME,$NEW_PATH_TO_THEME_ASSETS){\r\n\r\n	if($_SERVER[''HTTP_HOST'']=="localhost"){$WEBSITE_URL=$properties->WEBSITE_TEST_URL;}else{$WEBSITE_URL=$properties->WEBSITE_REMO_URL;}\r\n	switch($operation){\r\n		/* ----- THE TEMPLATE IS ----- */\r\n		/* ------- INSTRUCTIONS ------ */\r\n		/* \r\n			Replace ''METANAME'' with\r\n			the name of the item to \r\n			be displayed.\r\n			\r\n			This is from the $_GET vars.\r\n			\r\n			You can add modular div items\r\n			just by copying one of them\r\n			and translating it over. \r\n			Make sure to add to the CSS \r\n			file under customize css styles\r\n			for the page.\r\n		*/\r\n		/* ----- END INSTRUCTIONS ---- */\r\n		/*\r\n		case ''METANAME'':\r\n			$pname_entries_limit=getGlobalVars($properties,$pname.''_entries_limit'');\r\n			$credentials_list=explode(":::",$credentials);\r\n			$METANAME=$credentials_list[0];\r\n			$METANAMEfull=converter($properties,$METANAME,''url'',''from'');\r\n			echo "<div class=\\"back-button\\" onclick=\\"window.location.href=''".$wurl.$PADINFO."/".$page."''\\">&lt;</div>";\r\n			echo " <div class=\\"wyo-button\\">".$METANAMEfull."</div>";\r\n			echo "<br />";\r\n			echo "<br />";\r\n\r\n			\r\n			/* START BUILDING THE PNAME LIST */\r\n			/*\r\n			echo "<div class=\\"module-container-full\\">";\r\n				\r\n				echo "<div class=\\"module-container-full-row\\">";\r\n											\r\n					echo "<div class=\\"module-container-full-lcol\\">";\r\n						\r\n						//the dateandtime\r\n						echo "<div class=\\"module-container-full-date\\">";\r\n							\r\n						echo "</div>";\r\n						\r\n						//the author\r\n						echo "<div class=\\"module-container-full-author\\">";\r\n							\r\n						echo "</div>";\r\n						\r\n						//the comments\r\n						echo "<div class=\\"module-container-full-comments\\">";\r\n							\r\n						echo "</div>";\r\n						\r\n						//the category\r\n						echo "<div class=\\"module-container-full-category\\">";\r\n							\r\n						echo "</div>";\r\n						\r\n					echo "</div>";\r\n					\r\n					echo "<div class=\\"module-container-full-rcol\\">";\r\n						\r\n						//the title\r\n						echo "<div class=\\"module-container-full-title\\">";\r\n							\r\n						echo "</div>";\r\n						\r\n						//the content\r\n						echo "<div class=\\"module-container-full-content\\">";\r\n							\r\n						echo "</div>";\r\n						\r\n						//the tags\r\n						echo "<div class=\\"module-container-full-tags\\">";\r\n							\r\n						echo "</div>";\r\n						\r\n					echo "</div>";\r\n					\r\n				echo "</div>";\r\n				\r\n			echo "</div>";\r\n			echo "<br />";\r\n			*/\r\n			/* END BUILDING PNAME LIST */\r\n		/*\r\n		break;\r\n		*/\r\n		/* ----- END THE TEMPLATE----- */\r\n		\r\n		/* CUSTOM META OPTIONS GO HERE */\r\n		case ''category'':\r\n			echo "<a name=\\"top\\"></a>";\r\n			$pname_entries_limit=getGlobalVars($properties,$pname.''_entries_limit'');\r\n			$credentials_list=explode(":::",$credentials);\r\n			$category=$credentials_list[0];\r\n			$categoryfull=converter($properties,$category,''url'',''from'');\r\n			echo "<div class=\\"back-button\\" onclick=\\"window.location.href=''".$wurl.$PADINFO."/".$page."''\\">&lt;</div>";\r\n			echo " <div class=\\"wyo-button\\">".$categoryfull."</div>";\r\n			echo "<br />";\r\n			echo "<br />";\r\n			$pname_suburi="category/".$category;\r\n			$FIND_CAT_ID=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_categories WHERE shortname=''$category''");\r\n			if(mysql_num_rows($FIND_CAT_ID)<1){\r\n				/* NO ENTRIES; DISPLAY COOL NONE FOUND IMAGE */\r\n				echo "<img src=\\"".$WEBSITE_URL.$NEW_PATH_TO_THEME_ASSETS."/images/no_category_found.png\\" width=\\"100%\\" height=\\"400\\" title=\\"No Category were found in our database\\">";\r\n			} else {\r\n				$FETCH_CAT_ID=mysql_fetch_array($FIND_CAT_ID);\r\n				$categoryID=$FETCH_CAT_ID[''id''];\r\n				\r\n				/* BUILD PAGINATION */                                                                                            \r\n				$GET_ALLPNAME_ENTRIES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_entries WHERE category LIKE ''%$categoryID%'' AND status=''".$PNAME_PUBLISHED_NAME."''") or die(''uh oh! ''.mysql_error());\r\n				if(mysql_num_rows($GET_ALLPNAME_ENTRIES)<1){\r\n				/* NO ENTRIES; DONT DISPLAY PAGINATION */\r\n				} else {\r\n					$total_entries=mysql_num_rows($GET_ALLPNAME_ENTRIES);\r\n					$entries_per_page=getGlobalVars($properties,$pname.''_entries_limit'');\r\n					$num_of_btns=ceil($total_entries / $entries_per_page);\r\n					$pagination=$_GET[''pagination''];\r\n					$in_sets_of=getGlobalVars($properties,$pname.''_entries_in_sets_of'');\r\n					\r\n					if($pagination){$START=($pagination-1) * $entries_per_page;}else{$START=0;}\r\n					echo "<div id=\\"pagination\\">";\r\n						echo "<form action=\\"\\" method=\\"POST\\">";\r\n										\r\n						if(!isset($pagination)){$CURRENT_PAGINATION="1";}else{$CURRENT_PAGINATION=$pagination;}\r\n														\r\n						//previous arrow\r\n						if((!isset($pagination)) || ($pagination == "1")){echo "<input type=\\"button\\" disabled=\\"disabled\\" id=\\"pagination-page-back\\" name=\\"pagination-page-back\\" class=\\"pagination-link\\" value=\\"&lt;\\" onclick=\\"pagination(''back'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."'',''".$pname_suburi."/category/".$category."/'',''".$CURRENT_PAGINATION."'','''');\\"> ";}else{echo "<input type=\\"button\\" id=\\"pagination-page-back\\" name=\\"pagination-page-back\\" class=\\"pagination-link\\" value=\\"&lt;\\" onclick=\\"pagination(''back'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/category/".$category."/'','''',''".$CURRENT_PAGINATION."'','''');\\"> ";}\r\n						\r\n						//current pagination set\r\n						for($i=1; $i<=$in_sets_of; $i++){\r\n							if($i>=$num_of_btns){\r\n								/* EXCEEDED LIMIT */\r\n							} else {\r\n								if(($pagination != "") && ($pagination == $i)){echo "<input type=\\"button\\" disabled=\\"disabled\\" id=\\"pagination-page-{$i}\\" name=\\"pagination-page-{$i}\\" class=\\"pagination-link-selected\\" value=\\"".$i."\\" onclick=\\"pagination(''changeTo'',".$i.",''/".$pname_uri."/".$CURRENT_PAGINATION."category/".$category."/'','''');\\"> ";}else{\r\n									if(($i==1) && ($pagination == "")){echo "<input type=\\"button\\" id=\\"pagination-page-{$i}\\" name=\\"pagination-page-{$i}\\" class=\\"pagination-link-selected\\" disabled=\\"disabled\\" value=\\"".$i."\\" onclick=\\"pagination(''changeTo'',".$i.",''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/category/".$category."/'','''',''".$CURRENT_PAGINATION."'','''');\\"> ";}else{echo "<input type=\\"button\\" id=\\"pagination-page-{$i}\\" name=\\"pagination-page-{$i}\\" class=\\"pagination-link\\" value=\\"".$i."\\" onclick=\\"pagination(''changeTo'',".$i.",''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/category/".$category."/'','''',''".$CURRENT_PAGINATION."'','''');\\"> ";}\r\n								}	\r\n							}\r\n						}\r\n						\r\n						//next pagination set\r\n						echo "&nbsp;<span class=\\"pagination-link-null\\"> ... </span>&nbsp;&nbsp;";\r\n						\r\n						//last page\r\n						if($pagination == $num_of_btns){echo "<input type=\\"button\\" id=\\"pagination-page-last\\" name=\\"pagination-page-last\\" class=\\"pagination-link-selected\\" value=\\"".$num_of_btns."\\" onclick=\\"pagination(''last'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/category/".$category."/'','''',''".$CURRENT_PAGINATION."'',''".$num_of_btns."'');\\"> ";}else{echo "<input type=\\"button\\" id=\\"pagination-page-last\\" name=\\"pagination-page-last\\" class=\\"pagination-link\\" value=\\"".$num_of_btns."\\" onclick=\\"pagination(''last'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/category/".$category."/'','''',''".$CURRENT_PAGINATION."'',''".$num_of_btns."'');\\"> ";}\r\n						\r\n						//forward arrow\r\n						if($pagination == $num_of_btns){echo "<input type=\\"button\\" id=\\"pagination-page-forward\\" name=\\"pagination-page-forward\\" disabled=\\"disabled\\" class=\\"pagination-link\\" value=\\"&gt;\\" onclick=\\"pagination(''forward'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/category/".$category."/'','''',''".$CURRENT_PAGINATION."'','''');\\">";}else{echo "<input type=\\"button\\" id=\\"pagination-page-forward\\" name=\\"pagination-page-forward\\" class=\\"pagination-link\\" value=\\"&gt;\\" onclick=\\"pagination(''forward'',0,''".$WEBSITE_URL."'',''".$PADINFO."'',''/".$pname_uri."/category/".$category."/'','''',''".$CURRENT_PAGINATION."'','''');\\">";}\r\n						echo "</form>";\r\n					echo "</div>";\r\n				}\r\n				\r\n				/* END BUILD PAGINATION */\r\n				\r\n				if(mysql_num_rows($GET_ALLPNAME_ENTRIES)<1){\r\n					$THE_QUERY="SELECT * FROM {$properties->DB_PREFIX}".$pname."_entries WHERE category LIKE ''%$categoryID%'' AND status=''".$PNAME_PUBLISHED_NAME."'' ORDER BY dateandtime DESC";\r\n				} else {\r\n					$THE_QUERY="SELECT * FROM {$properties->DB_PREFIX}".$pname."_entries WHERE category LIKE ''%$categoryID%'' AND status=''".$PNAME_PUBLISHED_NAME."'' ORDER BY dateandtime DESC LIMIT {$START},{$entries_per_page}";\r\n				}\r\n				\r\n				$GET_PNAME_ENTRIES=mysql_query($THE_QUERY) or die(''uh oh! ''.mysql_error());\r\n				\r\n				if(mysql_num_rows($GET_PNAME_ENTRIES)<1){\r\n					/* NO ENTRIES; DISPLAY COOL NONE FOUND IMAGE */\r\n					echo "<img src=\\"".$WEBSITE_URL.$NEW_PATH_TO_THEME_ASSETS."/images/no_bloggies_found.png\\" width=\\"100%\\" height=\\"400\\" title=\\"No Entries were found in our database\\">";\r\n				} else {\r\n					while($FETCH_PNAME_ENTRIES=mysql_fetch_array($GET_PNAME_ENTRIES)){\r\n						$id=$FETCH_PNAME_ENTRIES[''id''];\r\n						$title=$FETCH_PNAME_ENTRIES[''title''];\r\n						$content=$FETCH_PNAME_ENTRIES[''content''];\r\n						\r\n						$author=$FETCH_PNAME_ENTRIES[''author''];\r\n						$director=$FETCH_PNAME_ENTRIES[''director''];\r\n						$studio=$FETCH_PNAME_ENTRIES[''studio''];\r\n						$network=$FETCH_PNAME_ENTRIES[''network''];\r\n						$category=$FETCH_PNAME_ENTRIES[''category''];\r\n						$tags=$FETCH_PNAME_ENTRIES[''tags''];\r\n						$dateandtime=$FETCH_PNAME_ENTRIES[''dateandtime''];\r\n						$dateandtime_goingtostart=$FETCH_PNAME_ENTRIES[''dateandtime_goingtostart''];\r\n					\r\n						//set up an indicator variable\r\n						$dateandtime_indicator=$dateandtime;\r\n						\r\n						if($dateandtime=="0000-00-00 00:00:00"){\r\n							/* ART Member has not started reviewing and will start */\r\n							//make dateandtime\r\n							//0000-00-00 00:00:00\r\n	