								\r\n									//compare								\r\n									$num_of_years=$pname_entry_year - $today_y;\r\n									$num_of_months=$pname_entry_month - $today_m;\r\n									$num_of_days=$pname_entry_day - $today_d;\r\n									if($num_of_years<1){\r\n										$ending_y="s";\r\n									} else if(($num_of_years>0) && ($num_of_years<2)){\r\n										$ending_y="";\r\n									} else if($num_of_years>1){\r\n										$ending_y="s";\r\n									}\r\n									\r\n									if($num_of_months<1){\r\n										$ending_m="s";\r\n\r\n									} else if(($num_of_months>0) && ($num_of_months<2)){\r\n										$ending_m="";\r\n									} else if($num_of_months>1){\r\n										$ending_m="s";\r\n									}\r\n									\r\n									if($num_of_days<1){\r\n										$ending_d="s";\r\n									} else if(($num_of_days>0) && ($num_of_days<2)){\r\n										$ending_d="";\r\n									} else if($num_of_days>1){\r\n										$ending_d="s";\r\n									}\r\n									\r\n									//make in...\r\n									if($num_of_years < 1){\r\n										$display_inyear="";\r\n									} else {\r\n										//check to see if month and day are there\r\n										if($num_of_months > 0 && $num_of_days > 0){\r\n											$display_inyear="{$num_of_years} year{$ending_y}, ";\r\n											$font_size="80%";\r\n										} else if($num_of_months > 0 || $num_of_days > 0){\r\n											$display_inyear="{$num_of_years} year{$ending_y}, ";\r\n											$font_size="100%";\r\n										} else {\r\n											$display_inyear="{$num_of_years} year{$ending_y}";	\r\n										}\r\n									}\r\n									if($num_of_months < 1){\r\n										$display_inmonth="";\r\n									} else {\r\n										//check to see if year and day are there\r\n										if($num_of_years > 0 && $num_of_days > 0){\r\n											$display_inmonth="{$num_of_months} month{$ending_m}, ";\r\n											$font_size="80%";\r\n										} else if($num_of_years > 0 || $num_of_days > 0){\r\n											$display_inmonth="{$num_of_months} month{$ending_m}, ";\r\n											$font_size="100%";\r\n										} else {\r\n											$display_inmonth="{$num_of_months} month{$ending_m}";\r\n										}\r\n									}\r\n									if($num_of_days < 1){\r\n										$display_inday="";\r\n									} else {\r\n										//check to see if year and month are there\r\n										if($num_of_years > 0 && $num_of_months > 0){\r\n											$display_intie=" &amp; ";\r\n											$display_inday="{$display_intie}{$num_of_days} day{$ending_d}";\r\n										} else {\r\n											$display_inday="{$num_of_days} day{$ending_d}";\r\n										}\r\n									}\r\n									echo "<div class=\\"module-container-full-date-goingtostart\\" style=\\"font-size: {$font_size};\\">";\r\n									//detect if date is today\r\n											if(($num_of_years == 0) && ($num_of_months == 0) && ($num_of_days == 0)){\r\n												echo "today";\r\n											} else {\r\n												echo "in {$display_inyear}{$display_inmonth}{$display_inday}";	\r\n											}\r\n									echo "</div>";\r\n								} else {\r\n									echo "<div class=\\"module-container-full-date\\">";\r\n									echo $dateandtime_d." ".$dateandtime_m_full.", ".$dateandtime_y;\r\n									echo "</div>";\r\n								}\r\n								\r\n								//the author or director\r\n								echo "<div class=\\"module-container-full-author\\">";\r\n									if($display_user===false){/*CUSTOM DISPLAY*/echo "<a href=\\"".$wurl.$PADINFO."/".$page."/directors/".$director_shortname."#top\\" title=\\"the director\\">" . $director_name . "</a>";}else{echo "<a href=\\"".$wurl.$PADINFO."/".$page."/user/".$uname."#top\\">" . $author_name . "</a>";}\r\n								echo "</div>";\r\n								\r\n								if($display_user===false){\r\n									//the studio\r\n									echo "<div class=\\"module-container-full-studio\\">";\r\n										echo "<a href=\\"".$wurl.$PADINFO."/".$page."/studios/".$studio_shortname."#top\\" title=\\"the studio\\">" . $studio_name . "</a>";\r\n									echo "</div>";\r\n									\r\n									//the network\r\n									echo "<div class=\\"module-container-full-network\\">";\r\n										echo "<a href=\\"".$wurl.$PADINFO."/".$page."/networks/".$network_shortname."#top\\" title=\\"the network\\">" . $network_name . "</a>";\r\n									echo "</div>";\r\n								} else if($display_user===true) {\r\n									/* NONE */	\r\n								}\r\n															\r\n								//the comments\r\n								echo "<div class=\\"module-container-full-comments\\">";\r\n									echo "<a href=\\"".$wurl.$PADINFO."/".$page."/permalink/".$pname_entry_year."/".$pname_entry_month."/".$pname_entry_day."/".converter($properties,$title,''url'',''to'')."#comments"."\\">".$comments_count."</a>";\r\n								echo "</div>";\r\n								\r\n								//the category\r\n								echo "<div class=\\"module-container-full-category\\">";\r\n									echo $pname_special." / " . $category;\r\n								echo "</div>";\r\n								\r\n							echo "</div>";\r\n							\r\n							echo "<div class=\\"module-container-full-rcol\\">";\r\n								\r\n								//the title\r\n								echo "<div class=\\"module-container-full-title\\">";\r\n	\r\n									echo "<a href=\\"".$wurl.$PADINFO."/".$page."/permalink/".$pname_entry_year."/".$pname_entry_month."/".$pname_entry_day."/".converter($properties,$title,''url'',''to'')."#top\\">".$title."</a>";\r\n								echo "</div>";\r\n								\r\n								//the content\r\n								echo "<div class=\\"module-container-full-content\\">";\r\n									$content=converter($properties,$content,''article'',''to'');\r\n									$content=converter($properties,$content,''basic'',''to'');\r\n									$content=converter($properties,$content,''ncode'',''to'');\r\n									\r\n									echo $content."...<a href=\\"".$wurl.$PADINFO."/".$page."/permalink/".$pname_entry_year."/".$pname_entry_month."/".$pname_entry_day."/".converter($properties,$title,''url'',''to'')."#top\\">[READ MORE]</a>";\r\n								echo "</div>";\r\n															\r\n								//the tags\r\n								echo "<div class=\\"module-container-full-tags\\">";\r\n									//convert the tags into urls\r\n									$tagslist=explode(",",$tags);\r\n									$tags="";\r\n									for($i=0; $i<count($tagslist); $i++){\r\n										$tags.="<a href=\\"".$wurl.$PADINFO."/".$page."/tag/".converter($properties,$tagslist[$i],''url'',''to'')."#top\\">".$tagslist[$i]."</a> ";\r\n									}\r\n							\r\n									echo "Tags: " . $tags;\r\n								echo "</div>";\r\n								\r\n							echo "</div>";\r\n							\r\n						echo "</div>";\r\n						\r\n					echo "</div>";\r\n					echo "<br />";\r\n					/* END BUILDING PNAME LIST */\r\n				}\r\n			}		\r\n		break;\r\n		/* END CUSTOM META OPTION ITEMS GO HERE */\r\n	}	\r\n}', '', '', '$GET_MODULES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}pages_modules WHERE page=''$page'' AND sidebar=''1'' AND launchpad=''$launchpadPN'' AND status=''Published'' AND toggle_visible=''on'' ORDER BY arr") or die(''uh oh! ''.mysql_error());\r\nwhile($FETCH_MODULES=mysql_fetch_array($GET_MODULES)){\r\n	$title=$FETCH_MODULES[''title''];\r\n	$contents=$FETCH_MODULES[''contents''];\r\n	$toggle_title=$FETCH_MODULES[''toggle_title''];\r\n	$toggle_visible=$FETCH_MODULES[''toggle_visible''];\r\n	if($toggle_title == "off"){/* no title */} else if($toggle_title == "on"){echo "<h2 class=\\"pages-sidebar-title\\">{$title}</h2>";}\r\n	echo "<div class=\\"pages-sidebar-contents\\">";\r\n		//convert contents\r\n		$contents=converter($properties,$contents,''basic'',''to'');\r\n		if($toggle_visible == "on"){eval($contents);}else if($toggle_visible == "off"){/* not visible */}\r\n	echo "</div>";\r\n}', '', '', '', '', 'yes', 'on', 'Published', 24, '127.0.0.1');
