INSERT INTO `h_pages_af_atoz_entries_lyrics` (`entry_id`, `content`, `type`, `language`) VALUES
(1, 'I heard your voice go hoarse as you screamed.\r\n<br />\r\n"Unravel the darkness that resides in your memories."\r\n<br />\r\nWhenever you took the last resort,\r\n<br />\r\nSomebody wound up getting hurt.\r\n<br />\r\nTrue, you may have lost sight of what''s true and what isn''t,\r\n<br />\r\nBut in order to regain your sparkle,\r\n<br />\r\nYou must cross over thousands of years worth of time.\r\n<br />\r\nWhile embracing the crimson shards that shall never return\r\n<br />\r\nYou continue on your wanderings.\r\n<br />\r\nUntil the day I can see you again and look into your eyes once more,\r\n<br />\r\nMy heart will be filled with\r\n<br />\r\nThe blue shard that you''ve been searching for.\r\n<br />\r\nEmbrace your memories\r\n<br />\r\nAs you embark on a never-ending journey.\r\n<br />\r\n', 'intro', 'english'),
(1, 'Kawaita sakebigoe ga kikoeta\r\n<br />\r\n"kioku no naka no yami wo hodoite"\r\n<br />\r\nItsumo saigo no kotae erande wa\r\n<br />\r\ndareka wo kizutsuketeta\r\n<br />\r\nSou nani ga shinjitsu ka wakaranai mama ni\r\n<br />\r\nKagayakimodosu tame\r\n<br />\r\nKimi wa ikusen no toki wo koete iku\r\n<br />\r\nModoranai aka no kakera\r\n<br />\r\nNigirishimete samayoitsuzuketeku\r\n<br />\r\nKimi wo utsushidaseru sono kitomi wo sagashidasu made\r\n<br />\r\nSagashiteta ao no kakera\r\n<br />\r\nMune ni afurete iku\r\n<br />\r\nKioku wo dakishimete\r\n<br />\r\nOwaru koto no nai tabi he to\r\n<br />\r\n', 'intro', 'japanese'),
(1, 'There''s a familiar forest in the distance,\r\n<br />\r\nthat smell of a gentle snowfall.\r\n<br />\r\nYou spin the threads of your broken memories,\r\n<br />\r\nsearching for a new path.\r\n<br />\r\nThough free now from your old destiny,\r\n<br />\r\nyour eyes are are blinded by their own determination.\r\n<br />\r\nA tiny point of light sparkles in the distance.\r\n<br />\r\nThe closer you try to get, the farther away it goes.\r\n<br />\r\nBut as long as you continue to shed such warm tears,\r\n<br />\r\na beautiful hymn will ring out from your soul,\r\n<br />\r\nslicing through the inky blackness.\r\n<br />\r\nRed from crying, your eyes look towards the heavens\r\n<br />\r\nas you walk down the road leading to tomorrow.', 'outro', 'english'),
(1, 'Tooi natsukashiki keshiki\r\n<br />\r\nYasashii yuki no kaori\r\n<br />\r\nTogire logire no kioku wo\r\n<br />\r\nTsumuide michi wo sagasu\r\n<br />\r\nKuruidashita sadame ni\r\n<br />\r\nTashika na menomae mo nanimokamo mienai\r\n<br />\r\nTookute mienai chiisana tomoshibi\r\n<br />\r\nNegau hodo miushinau mono\r\n<br />\r\nFukai yami wo kirisaiteku koto wa\r\n<br />\r\nKoko ni aru to kanaderu sanbika atatakai namida\r\n<br />\r\nKobore-ochiru ikiyuku kagiri\r\n<br />\r\nSora wo aogu akai hitomi ni tsuketa\r\n<br />\r\nAsu he no michi arukidasu', 'outro', 'japanese');
