 */\r\n	break;\r\n	\r\n	case ''single'':\r\n		/* FOR SINGLE ROW ITEMS (EG. PORTFOLIO OR CATEGORY) */\r\n		echo "<div class=\\"module-container-full\\">";\r\n			echo "<div class=\\"module-full-row\\">";										\r\n				\r\n				echo "<div class=\\"module-container-full-lcol\\">";\r\n					echo "<h2>Name</h2>";\r\n				echo "</div>";\r\n								\r\n				echo "<div class=\\"module-container-full-rcol\\">";					\r\n					//the title\r\n					echo "<div class=\\"module-container-full-title\\">";\r\n						if($pname=="pages"){\r\n							/* SPECIAL INSTRUCTIONS : PAGES */\r\n							//get the lpm\r\n							$GET_LPM=mysql_query("SELECT * FROM {$properties->DB_PREFIX}launchpads WHERE id=''$lpm''");\r\n							$FETCH_LPM=mysql_fetch_array($GET_LPM);\r\n							$lpm=$FETCH_LPM[''short''];\r\n							echo "<a href=\\"".$WEBSITE_URL.$lpm."/".$page."\\">" . $pageNAME . "</a>";\r\n						} else {\r\n							if($display_user===true){\r\n								echo "<a href=\\"".$WEBSITE_URL.$PADINFO."/".$pname_uri."/folio/".converter($properties,$name,''url'',''to'')."\\">" . $name . "</a>";\r\n							} else {\r\n								echo "<a href=\\"".$WEBSITE_URL.$PADINFO."/".$pname_uri."/category/".$shortname."\\">" . $name . "</a>";	\r\n							}	\r\n						}\r\n					echo "</div>";																	\r\n				echo "</div>";				\r\n			echo "</div>";			\r\n		echo "</div>";\r\n		echo "<br />";\r\n	break;\r\n	\r\n	case ''double'':\r\n		/* FOR MORE COMPLEX DOUBLE ROW ITEMS (EG. USERS) */\r\n		/* START BUILDING LIST */\r\n		echo "<div class=\\"module-container-full\\">";\r\n			echo "<div class=\\"module-container-full-row\\">";\r\n				echo "<div class=\\"module-container-full-lcol\\">";\r\n					echo "<h2>".$uname."</h2>";\r\n				echo "</div>";\r\n				\r\n				echo "<div class=\\"module-container-full-rcol\\">";\r\n					\r\n					//the title\r\n					echo "<div class=\\"module-container-full-title\\">";\r\n						echo $fname." ".$lname;\r\n						$GET_STAFF_TYPE_NAME=mysql_query("SELECT * FROM {$properties->DB_PREFIX}staff_types WHERE