(1, 4, 'padmain', 'Colleagues of Mine', '', 'on', '//GET COLLEAGUES OF MINE MAKER\r\necho "<div class=\\"blog-hp-container-title\\">Blog Roll</div>";\r\necho "<div class=\\"module-container-mini\\">";\r\n	echo "<div class=\\"module-container-mini-lcol\\">";						\r\n		/* CUSTOM CODE GOES HERE */		\r\n		echo "<div class=\\"module-container-mini-content\\">";\r\n		$GET_COM_QUERY=mysql_query("SELECT * FROM h_links WHERE type=''blogroll''") or die(mysql_error());\r\n		if(mysql_num_rows($GET_COM_QUERY)<1){\r\n		  echo "<li>no items</li>";\r\n		} else {\r\n			@$ending="";\r\n		  while($FETCH_COM_QUERY=mysql_fetch_array($GET_COM_QUERY)){\r\n			if(strlen($FETCH_COM_QUERY[''name'']) > 25){$ending="...";}else{$ending="";}\r\n			echo "<li class=\\"module-li\\"><a href=\\"".$FETCH_COM_QUERY[''url'']."\\" target=\\"".$FETCH_COM_QUERY[''target'']."\\" title=\\"".$FETCH_COM_QUERY[''title'']."\\">".substr($FETCH_COM_QUERY[''name''],0,25).$ending."</a></li>";\r\n		   }\r\n		}\r\n		echo "</div>";\r\n		/* END CUSTOM CODE GOES HERE */\r\n	echo "</div>";\r\necho "</div>";', 'blog', 'sidebar', 1, '', 'on', 'no', 'Published'),
(2, 1, 'padmain', 'Back on the Road', '', 'on', '/* YOU MIGHT HAVE TO SEARCH FOR THE PAD NAME AND CHANGE IT TO MATCH WHAT IS NEEDED */\r\n$pname="blog";\r\n$pname_special="Blog";\r\n$pname_uri="blog";\r\n$display_user=true;\r\n$PADINFO=$properties->PADMAIN;\r\n/* ---------- DO NOT EDIT BELOW THIS LINE OR ELSE YOU WILL BREAK IT --------- */\r\n//BACK ON THE ROAD MAKER\r\n\r\n/* STEP 1: COLLECTION INFORMATION FROM DB */\r\n\r\n/* STEP 1.1: FIND ALL THE DATES AND PUT THEM INTO A STRING*/\r\n$FIND_ALL_DATE_IN_ENTRIES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_entries WHERE status=''Published'' AND dateandtime LIKE ''%'' GROUP BY date_year, date_month ORDER BY dateandtime DESC");\r\n@$dateandtime_string="";\r\nwhile($FETCH_ADIE=mysql_fetch_array($FIND_ALL_DATE_IN_ENTRIES)){\r\n	$dateandtime=$FETCH_ADIE[''dateandtime'']; \r\n	/* STEP 2: CONVERT, STREAMLINE, AND BUILD DATES */\r\n	/*\r\n	HELPFUL COMMENTS :\r\n	0000-00-00\r\n	0123456789\r\n	*/\r\n\r\n	//years\r\n	$year=substr($dateandtime,0,4); //2012\r\n\r\n	//months\r\n	$month=substr($dateandtime,5,2); //08\r\n	\r\n	//YYYY-MM,YYYY-MM,\r\n	\r\n	$this_dateandtime_string=$year."-".$month."-";\r\n		\r\n	//put it together\r\n	$dateandtime_string.=$year."-".$month.",";\r\n}\r\n\r\n/* STEP 3: PARSE THE INFORMATION INTO MANAGEABLE LISTS */\r\n@$the_complete_dateandtime=$dateandtime_string;\r\n@$the_complete_dateandtime_list=explode(",",$the_complete_dateandtime);\r\n@$new_complete_dateandtime="";\r\n\r\n/* STEP X: TAKE THE LIST AND COUNT HOW MANY TIMES A STR APPEARS */\r\nfor($ic=0; $ic<count($the_complete_dateandtime_list); $ic++){\r\n	@$count_o=substr_count($the_complete_dateandtime,$the_complete_dateandtime_list[$ic]);\r\n}\r\n\r\n/* STEP 4: MAKE THE LIST AND DO ADDITIONAL PARSING IF REQUIRED */\r\n/* STEP 4.1: PARSE EACH YEAR AND MONTH */\r\necho "<div class=\\"blog-hp-container-title\\">Blog Archive</div>";\r\necho "<div class=\\"module-container-twocolumn\\">";\r\n	echo "<div class=\\"module-container-twocolumn-lcol\\">";\r\n		/* CUSTOM CODE GOES HERE */\r\n		echo "<div class=\\"module-container-twocolumn-lcol-content\\">";\r\n		for($i=0; $i<count($the_complete_dateandtime_list); $i++){\r\n			@$the_exploded_list=explode("-",$the_complete_dateandtime_list[$i]);\r\n			@$the_year=$the_exploded_list[0];\r\n			@$the_month=$the_exploded_list[1];\r\n			$default_month_num_list=array(\r\n				0 => "01",\r\n				1 => "02",\r\n				2 => "03",\r\n				3 => "04",\r\n				4 => "05",\r\n				5 => "06",\r\n				6 => "07",\r\n				7 => "08",\r\n				8 => "09",\r\n				9 => "10",\r\n				10 => "11",\r\n				11 => "12"\r\n			);\r\n			$default_month_names_list=array(\r\n				0 => "January",\r\n				1 => "February",\r\n				2 => "March",\r\n				3 => "April",\r\n				4 => "May",\r\n				5 => "June",\r\n				6 => "July",\r\n				7 => "August",\r\n				8 => "September",\r\n				9 => "October",\r\n				10 => "November",\r\n				11 => "December"\r\n			);\r\n			for($ii=0; $ii<count($default_month_num_list); $ii++){\r\n				if($the_month==$default_month_num_list[$ii]){\r\n					$the_month=$default_month_names_list[$ii];\r\n				}\r\n			}\r\n			echo "<li class=\\"module-container-twocolumn-lcol-content-li\\"><a href=\\"".$wurl.$PADINFO."/".$page."/botr/".$the_year."/".$the_month."#top\\">".$the_month." ".$the_year."</a></li>";\r\n		}\r\n		echo "</div>";\r\n		/* END CUSTOM CODE GOES HERE */\r\n	echo "</div>";\r\n	echo "<div class=\\"module-container-twocolumn-rcol\\">";\r\n		/* CUSTOM CODE GOES HERE */\r\n		echo "<div class=\\"module-container-twocolumn-rcol-content\\">";\r\n		/* STEP 4.2: COUNT THE NUMBER OF ENTRIES IN EACH MONTH YEAR */\r\n		for($i=0; $i<count($the_complete_dateandtime_list)-1; $i++){\r\n				@$the_exploded_list=explode("-",$the_complete_dateandtime_list[$i]);\r\n				@$the_year=$the_exploded_list[0];\r\n				@$the_month=$the_exploded_list[1];\r\n				$FIND_ALL_ENTRIES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_entries WHERE dateandtime LIKE ''$the_year-$the_month-%'' AND status=''Published''");\r\n				$the_count=mysql_num_rows($FIND_ALL_ENTRIES);\r\n				echo "<li class=\\"module-container-twocolumn-rcol-content-li-paren\\">(".$the_count.")</a></li>";\r\n			}				\r\n		echo "</div>";\r\n		/* END CUSTOM CODE GOES HERE */\r\n	echo "</div>";\r\necho "</div>";', 'blog', 'sidebar', 1, '', 'on', 'no', 'Published'),
(3, 3, 'padmain', 'Creative Fotos', '', 'on', 'echo "<div class=\\"blog-hp-container-title\\">Blog Photos</div>";\r\necho "<div class=\\"module-container-mini\\">";\r\n	/* CUSTOM CODE GOES HERE */\r\n	$BlogFlickrID=getGlobalVars($properties,"BlogFlickrID");\r\n	$BlogFlickrName=getGlobalVars($properties,"BlogFlickrName");\r\n			\r\n	if(strlen($BlogFlickrName)>15){$BlogFlickrNameReal=substr($BlogFlickrName,0,15)."...";}else{$BlogFlickrNameReal=$BlogFlickrName;}\r\n	echo "<div class=\\"module-container-mini-lcol\\">";						\r\n		echo "<div class=\\"module-container-mini-content\\">\r\n			";\r\n			echo "\r\n			<ul id=\\"cycle\\"></ul>\r\n			<script type=\\"text/javascript\\">\r\n			$(''#cycle'').jflickrfeed({\r\n				limit: 50,\r\n				qstrings: {\r\n					id: ''".$BlogFlickrID."''\r\n				},\r\n				itemTemplate: ''<li><img src=\\"{{image}}\\" alt=\\"{{title}}\\" width=\\"195\\" height=\\"255\\" /><div>{{title}}</div></li>''\r\n			}, function(data) {\r\n				$(''#cycle div'').hide();\r\n				$(''#cycle'').cycle({\r\n					timeout: 5000\r\n				});\r\n				$(''#cycle li'').hover(function(){\r\n					$(this).children(''div'').show();\r\n				},function(){\r\n					$(this).children(''div'').hide();\r\n				});\r\n			});\r\n			</script>\r\n			<br />\r\n			&nbsp;&nbsp;&nbsp;<a href=\\"http://instagram.com/".$BlogFlickrName."\\" class=\\"big-url\\" target=\\"_blank\\">Follow @".$BlogFlickrNameReal."</a>\r\n			<br />\r\n			<br />\r\n			";\r\n		echo "</div>";\r\n	echo "</div>";\r\n	/* END CUSTOM CODE GOES HERE */\r\necho "</div>";', 'blog', 'sidebar', 1, '', 'on', 'no', 'Published'),
(4, 6, 'padmain', 'New Memories', '', 'on', '/* NEW MEMORIES MODULE */\r\necho "<div class=\\"blog-hp-container-title\\">New Blog Posts</div>";\r\necho "<div class=\\"module-container-mini\\">";\r\n	echo "<div class=\\"module-container-mini-leftcol\\">";\r\n		/* CUSTOM CODE GOES HERE */\r\n		$GET_LATEST_BLOG_ENTRIES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}blog_entries WHERE status=''Published'' ORDER BY dateandtime DESC LIMIT 2") or die(''uh oh! ''.mysql_error());\r\n		if(mysql_num_rows($GET_LATEST_BLOG_ENTRIES)<1){\r\n			echo "No memories found...:(";\r\n		} else {\r\n			while($FETCH_LATEST_BLOG_ENTRIES=mysql_fetch_array($GET_LATEST_BLOG_ENTRIES)){\r\n				$id=$FETCH_LATEST_BLOG_ENTRIES[''id''];\r\n				$title=$FETCH_LATEST_BLOG_ENTRIES[''title''];\r\n				$content=$FETCH_LATEST_BLOG_ENTRIES[''content''];\r\n				$author=$FETCH_LATEST_BLOG_ENTRIES[''author''];\r\n				$category=$FETCH_LATEST_BLOG_ENTRIES[''category''];\r\n				$tags=$FETCH_LATEST_BLOG_ENTRIES[''tags''];\r\n				$dateandtime=$FETCH_LATEST_BLOG_ENTRIES[''dateandtime''];\r\n			\r\n				//make dateandtime\r\n				//0000-00-00\r\n				//0123456789\r\n				$blog_entry_year  = substr($dateandtime,0,4);\r\n				$blog_entry_month = substr($dateandtime,5,2);\r\n				$blog_entry_day   = substr($dateandtime,8,2);\r\n				//determine month					\r\n				if($blog_entry_month=="01"){$blog_entry_month_full="Jan";}\r\n				if($blog_entry_month=="02"){$blog_entry_month_full="Feb";}\r\n				if($blog_entry_month=="03"){$blog_entry_month_full="Mar";}\r\n				if($blog_entry_month=="04"){$blog_entry_month_full="Apr";}\r\n				if($blog_entry_month=="05"){$blog_entry_month_full="May";}\r\n				if($blog_entry_month=="06"){$blog_entry_month_full="Jun";}\r\n				if($blog_entry_month=="07"){$blog_entry_month_full="Jul";}\r\n				if($blog_entry_month=="08"){$blog_entry_month_full="Aug";}\r\n				if($blog_entry_month=="09"){$blog_entry_month_full="Sep";}\r\n				if($blog_entry_month=="10"){$blog_entry_month_full="Oct";}\r\n				if($blog_entry_month=="11"){$blog_entry_month_full="Nov";}\r\n				if($blog_entry_month=="12"){$blog_entry_month_full="Dec";}\r\n			\r\n				//convert dateandtime into a better looking string\r\n				$dateandtime = $blog_entry_month . " " . $blog_entry_day . ", " . $blog_entry_year;\r\n				$dateandtime_m = $blog_entry_month;\r\n				$dateandtime_m_full = $blog_entry_month_full;\r\n				$dateandtime_d = $blog_entry_day;\r\n				$dateandtime_y = $blog_entry_year;\r\n			\r\n				//convert and get the author\r\n				$GET_BLOG_AUTHOR_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}users WHERE id=''{$author}''") or die(''uh oh! ''.mysql_error());\r\n				while($FETCH_BLOG_AUTHOR_INFO=mysql_fetch_array($GET_BLOG_AUTHOR_INFO)){\r\n				 $uname=$FETCH_BLOG_AUTHOR_INFO[''uname''];\r\n				 $fname=$FETCH_BLOG_AUTHOR_INFO[''fname''];\r\n				 $lname=$FETCH_BLOG_AUTHOR_INFO[''lname''];\r\n				 $HTDN=$FETCH_BLOG_AUTHOR_INFO[''how_to_display_name''];\r\n				}	\r\n			\r\n				//convert and get the category\r\n				$GET_BLOG_CATEGORY_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}blog_categories WHERE id=''{$category}''") or die(''uh oh! ''.mysql_error());\r\n				while($FETCH_BLOG_CATEGORY_INFO=mysql_fetch_array($GET_BLOG_CATEGORY_INFO)){\r\n				 $category=$FETCH_BLOG_CATEGORY_INFO[''name''];\r\n				}\r\n				\r\n				//get comment for posts\r\n				$GET_COMMENTS_C=mysql_query("SELECT * FROM {$properties->DB_PREFIX}blog_comments WHERE entry_id=''$id'' AND status=''Approved''");\r\n				if(mysql_num_rows($GET_COMMENTS_C)<1){\r\n					$comments_count="0 comments";\r\n				} else {\r\n					if((mysql_num_rows($GET_COMMENTS_C)>0) && (mysql_num_rows($GET_COMMENTS_C)<2)){\r\n						$comments_count=mysql_num_rows($GET_COMMENTS_C)." comment";\r\n					} else {\r\n						$comments_count=mysql_num_rows($GET_COMMENTS_C)." comments";\r\n					}\r\n				}\r\n				\r\n				//set author to uname from db\r\n				/* SWITCH DEPENDING ON USER-SET ATTIBUTE */switch($HTDN){case ''full'':$author=$fname." ".$lname;break;case ''only username'':$author=$uname;break;case ''only first name'':$author=$fname;break;}\r\n				//set author to uname from db\r\n				echo "<div class=\\"module-container-mini-title\\">".$title."</div>";\r\n				\r\n				echo "<div class=\\"module-container-mini-author\\">by <a href=\\"".@$wurl.$properties->PADMAIN."/blog/user/".$uname."#top\\">" . $author . "</a></div>";\r\n				\r\n				echo "<div class=\\"module-container-mini-category\\">under " . "<a href=\\"".@$wurl.$properties->PADMAIN."/blog/category/".strtolower(converter($properties,$category,''url'',''to''))."#top\\">" . $category . "</a></div>";\r\n				\r\n				echo "<div class=\\"module-container-mini-content\\">".substr(converter($properties,$content,''previewarticle'',''to''),0,100)."...</div>";\r\n				\r\n				echo "<div class=\\"module-container-mini-readmorebtn\\">";\r\n					\r\n					echo "<div class=\\"module-container-mini-rmbtnrow\\">";\r\n						echo "<div class=\\"module-container-mini-rmbtnlcol\\">&nbsp;</div>";\r\n						echo "<div class=\\"module-container-mini-rmbtnrcol\\" onclick=\\"".@$wurl.$properties->PADMAIN."/blog/permalink/".converter($properties,$title,''url'',''to'')."\\"><a href=\\"".@$wurl.$properties->PADMAIN."/blog/permalink/".$blog_entry_year."/".$blog_entry_month."/".$blog_entry_day."/".converter($properties,$title,''url'',''to'')."#top\\">Check it out!</a></div>";\r\n					echo "</div>";\r\n					\r\n				echo "</div>";\r\n				/* END CUSTOM CODE GOES HERE */\r\n				echo "<div class=\\"module-container-mini-separator\\"></div>";\r\n			}\r\n		}\r\n	echo "</div>";\r\necho "</div>";', 'blog', 'sidebar', 1, '', 'on', 'no', 'Published'),
(5, 5, 'padmain', 'The Library', '', 'on', '//THE LIBRARY MAKER\r\n$GET_LIB_QUERY=mysql_query("SELECT * FROM {$properties->DB_PREFIX}blog_categories WHERE status = ''Active'' ORDER BY name") or die(mysql_error());\r\necho "<div class=\\"blog-hp-container-title\\">Blog Categories</div>";\r\necho "<div class=\\"module-container-twocolumn\\">";\r\n	if(mysql_num_rows($GET_LIB_QUERY)<1){\r\n		echo "No Categories Yet!";\r\n	} else {		\r\n		echo "<div class=\\"module-container-twocolumn-lcol\\">";						\r\n			/* CUSTOM CODE GOES HERE */		\r\n			echo "<div class=\\"module-container-twocolumn-lcol-content\\">";\r\n				@$the_complete_list_of_cats="";\r\n					\r\n				//put each year and month\r\n				while($FETCH_LIB_QUERY=mysql_fetch_array($GET_LIB_QUERY)){\r\n					$id=$FETCH_LIB_QUERY[''id''];\r\n					$name=$FETCH_LIB_QUERY[''name''];\r\n					$shortname=$FETCH_LIB_QUERY[''shortname''];\r\n					$parentid=$FETCH_LIB_QUERY[''parentid''];\r\n					\r\n					if($parentid==0){\r\n						//not linked to a parent; it is a parent\r\n						echo "<li class=\\"module-container-twocolumn-lcol-li\\"><a href=\\"".@$wurl.$properties->PADMAIN."/".$page."/category/".$FETCH_LIB_QUERY[''shortname'']."#top\\">".$FETCH_LIB_QUERY[''name'']."</a></li>";\r\n						//make a list of the cats\r\n						$the_complete_list_of_cats.=$id.",";\r\n					} else {\r\n						//linked to a parent\r\n						//find the parent name\r\n						$GET_PARENT_QUERY=mysql_query("SELECT * FROM {$properties->DB_PREFIX}blog_categories WHERE status = ''Active'' AND id=''$parentid''") or die(mysql_error());\r\n						$FETCH_PARENT_QUERY=mysql_fetch_array($GET_PARENT_QUERY);\r\n						$parentname=$FETCH_PARENT_QUERY[''shortname''];\r\n						\r\n						echo "<li class=\\"module-container-twocolumn-lcol-li\\"><a href=\\"".@$wurl.$properties->PADMAIN."/".$page."/category/".$parentname.":".$FETCH_LIB_QUERY[''shortname'']."#top\\">".$FETCH_LIB_QUERY[''name'']."</a></li>";	\r\n						\r\n						//make a list of the cats\r\n						$the_complete_list_of_cats.=$id.",";\r\n					}\r\n				}\r\n			echo "</div>";\r\n			/* END CUSTOM CODE GOES HERE */\r\n		echo "</div>";\r\n		echo "<div class=\\"module-container-twocolumn-rcol\\">";\r\n			/* CUSTOM CODE GOES HERE */		\r\n			echo "<div class=\\"module-container-twocolumn-rcol-content\\">";\r\n				//explode the list\r\n				$the_complete_list_of_cats_list=explode(",",$the_complete_list_of_cats);\r\n				for($i=0; $i<count($the_complete_list_of_cats_list)-1; $i++){\r\n					$COUNT_ENTRIES_BY_CAT=mysql_query("SELECT * FROM {$properties->DB_PREFIX}blog_entries WHERE status=''Published'' AND category=''$the_complete_list_of_cats_list[$i]''");\r\n					$the_count=mysql_num_rows($COUNT_ENTRIES_BY_CAT);\r\n					echo "<li class=\\"module-container-twocolumn-rcol-content-li-paren\\">(".$the_count.")</li>";\r\n				}\r\n			echo "</div>";\r\n			/* END CUSTOM CODE GOES HERE */\r\n		echo "</div>";\r\n	}\r\necho "</div>";', 'blog', 'sidebar', 1, '', 'on', 'no', 'Published'),
