irst name'':$author_name=$fname;break;}}\r\n	\r\n					/* START BUILDING THE PNAME LIST */\r\n					//echo "<div class=\\"".$title_name."-hp-container-title\\">".ucfirst($title_name)." Post</div>";\r\n					echo "<div class=\\"module-container-small whatido_bottom\\">";\r\n						\r\n						echo "<div class=\\"module-container-small-row\\">";\r\n													\r\n							echo "<div class=\\"module-container-small-lcol\\">";\r\n								\r\n								//the dateandtime\r\n								if($dateandtime_indicator=="0000-00-00 00:00:00"){\r\n									//get todays date in pieces\r\n									$today_y=date("Y");\r\n									$today_m=date("n");\r\n									$today_d=date("j");\r\n									\r\n									//establish vars\r\n									$font_size="80%";\r\n									\r\n									//compare								\r\n									$num_of_years=$pname_entry_year - $today_y;\r\n									$num_of_months=$pname_entry_month - $today_m;\r\n									$num_of_days=$pname_entry_day - $today_d;\r\n									if($num_of_years<1){\r\n										$ending_y="s";\r\n									} else if(($num_of_years>0) && ($num_of_years<2)){\r\n										$ending_y="";\r\n									} else if($num_of_years>1){\r\n										$ending_y="s";\r\n									}\r\n									\r\n									if($num_of_months<1){\r\n										$ending_m="s";\r\n									} else if(($num_of_months>0) && ($num_of_months<2)){\r\n										$ending_m="";\r\n									} else if($num_of_months>1){\r\n										$ending_m="s";\r\n									}\r\n									\r\n									if($num_of_days<1){\r\n										$ending_d="s";\r\n									} else if(($num_of_days>0) && ($num_of_days<2)){\r\n										$ending_d="";\r\n									} else if($num_of_days>1){\r\n										$ending_d="s";\r\n									}\r\n									\r\n									//make in...\r\n									if($num_of_years < 1){\r\n										$display_inyear="";\r\n									} else {\r\n										//check to see if month and day are there\r\n										if($num_of_months > 0 && $num_of_days > 0){\r\n											$display_inyear="{$num_of_years} year{$ending_y}, ";\r\n											$font_size="