('off', 'about', 'double', 'About', 'about,what is witll?,WITLL,CMS ABOUT,Web Interface To Learn &amp; to Love,', 'padmain', 1, '', '2012-07-26', '<h2 class="pages-maincontent-title">About WITLL</h2>\r\n<div class="pages-maincontent-content">\r\n<center><h1>Currently working on this page</h1></center>\r\n</div>', '', '\r\n', '', '$GET_MODULES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}pages_modules WHERE page=''$page'' AND sidebar=''1'' AND launchpad=''$launchpadPN'' ORDER BY arr") or die(''uh oh! ''.mysql_error());\r\nwhile($FETCH_MODULES=mysql_fetch_array($GET_MODULES)){\r\n	$title=$FETCH_MODULES[''title''];\r\n	$contents=$FETCH_MODULES[''contents''];\r\n	$toggle_title=$FETCH_MODULES[''toggle_title''];\r\n	$toggle_visible=$FETCH_MODULES[''toggle_visible''];\r\n	if($toggle_title == "off"){/* no title */} else if($toggle_title == "on"){echo "<h2 class=\\"pages-sidebar-title\\">{$title}</h2>";}\r\n	echo "<div class=\\"pages-sidebar-contents\\">";\r\n		//convert contents\r\n		$contents=converter($properties,$contents,''basic'',''to'');\r\n		if($toggle_visible == "on"){eval($contents);}else if($toggle_visible == "off"){/* not visible */}\r\n	echo "</div>";\r\n}', '', '', '', '', 'yes', 'off', 'on'),
('off', 'contact', 'double', 'Contact', 'contact Nat4an,contact Nathan Smyth,contacting Nat4an Corp', 'padmain', 1, '', '2012-08-12', '', '$TITLE="Contact me";\r\n$PADINFO=$properties->PADMAIN;\r\n$DBTBLSTART="";\r\n$EXTRA_NAME=""; //for the email subject\r\n$EXTRA_NAME_SAFE=""; //the safe version of $EXTRA_NAME\r\nglobal $FORMNAME;\r\n$FORMNAME="contact"; //usually the page name\r\n$POC_DB="users"; //could be: users, tmm_virtuosos, etc\r\n$POC_DB_WHERE_TYPE_CLAUSE="type = ''admin''"; //the where clause of the poc db\r\n$POC_DB_ORDER_BY="uname";\r\n/* ---------- DO NOT EDIT BELOW THIS LINE OR ELSE YOU WILL BREAK IT --------- */\r\nif(isset($_GET[''meta''])){\r\nswitch($_GET[''meta'']){\r\n	case ''checkstatus'':\r\n		@$ticket_id="";\r\n		echo "<br />";\r\n		echo "<div class=\\"back-button\\" onclick=\\"window.location.href=''".@$properties->WEBSITE_URL.$PADINFO."/".@$page."''\\">&lt;</div>";\r\n		if($ticket_id == ""){echo " <div class=\\"wyo-button\\">Check Status of a Ticket</div>";}else{$ticket_id=$_GET[''ticketid''];echo " <div class=\\"wyo-button\\">Check Status of Ticket ID: <b>".substr($ticket_id,0,40)."<a title=\\"{$ticketid}\\" class=\\"white-url\\">...</a></b></div>";}\r\n		echo "<br />";\r\n		echo "<br />";\r\n		if((isset($_GET[''ticketid''])) || (@$_POST[''ticket_id_check''])){\r\n			if(isset($_GET[''ticketid''])){$ticket_id=$_GET[''ticketid''];}else if(isset($_POST[''ticket_id_check''])) {if($_POST[''contact_yemail''] != "Enter Your Email"){/* LOOKUP BY EMAIL */$GET_TICKET_BY_EMAIL=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$DBTBLSTART."queries WHERE contact_email=''".$_POST[''contact_yemail'']."'' ORDER BY dateandtime DESC") or die(''uh oh! ''.mysql_error());if(mysql_num_rows($GET_TICKET_BY_EMAIL)<1){echo "<div id=\\"formLayoutTableSingle\\"><div class=\\"formLayoutTableRowSingle\\"><div class=\\"formLayoutTableRowLeftColSingle\\">Response</div><div class=\\"formLayoutTableRowRightColSingle\\">Sorry I could not find a Ticket ID under that email