('off', 'changes', 'single', 'Site Changes', 'site changes, changelog,', 'padmain', 1, '', '2012-09-14', '', '@$year=$_GET[''year''];\r\n@$month=$_GET[''month''];\r\n@$day=$_GET[''day''];\r\nif($month=="Jan"){$month="01";}\r\nif($month=="Feb"){$month="02";}\r\nif($month=="Mar"){$month="03";}\r\nif($month=="Apr"){$month="04";}\r\nif($month=="May"){$month="05";}\r\nif($month=="Jun"){$month="06";}\r\nif($month=="Jul"){$month="07";}\r\nif($month=="Aug"){$month="08";}\r\nif($month=="Sep"){$month="09";}\r\nif($month=="Oct"){$month="10";}\r\nif($month=="Nov"){$month="11";}\r\nif($month=="Dec"){$month="12";}\r\n@$date=$year."-".$month."-".$day;\r\necho "<h2 class=\\"pages-maincontent-title\\">Site Changes</h2>";\r\necho "<div class=\\"pages-maincontent-content\\">";\r\n$GET_CHANGELOG=mysql_query("SELECT * FROM {$properties->DB_PREFIX}changelog WHERE dateandtime LIKE ''$date%'' ORDER BY dateandtime DESC");\r\nif(mysql_num_rows($GET_CHANGELOG)<1){\r\necho "No changes made on this date!";\r\n} else {\r\nwhile($FETCH_CHANGELOG=mysql_fetch_array($GET_CHANGELOG)){\r\n	@$dateandtime=$FETCH_CHANGELOG[''dateandtime''];\r\n	//make dateandtime\r\n	//0000-00-00 00:00:00\r\n	//0123456789012345678\r\n	$changes_year  = substr($dateandtime,0,4);\r\n	$changes_month = substr($dateandtime,5,2);\r\n	$changes_day   = substr($dateandtime,8,2);\r\n	$changes_hour  = substr($dateandtime,11,2);\r\n	$changes_min   = substr($dateandtime,14,2);\r\n	$changes_sec   = substr($dateandtime,17,2);\r\n		\r\n	@$changes_month_full="";\r\n	//determine month				\r\n	if($changes_month=="01"){$changes_month_full="January";}\r\n	if($changes_month=="02"){$changes_month_full="February";}\r\n	if($changes_month=="03"){$changes_month_full="March";}\r\n	if($changes_month=="04"){$changes_month_full="April";}\r\n	if($changes_month=="05"){$changes_month_full="May";}\r\n	if($changes_month=="06"){$changes_month_full="June";}\r\n	if($changes_month=="07"){$changes_month_full="July";}\r\n	if($changes_month=="08"){$changes_month_full="August";}\r\n	if($changes_month=="09"){$changes_month_full="September";}\r\n	if($changes_month=="10"){$changes_month_full="October";}\r\n	if($changes_month=="11"){$changes_month_full="November";}\r\n	if($changes_month=="12"){$changes_month_full="December";}\r\n	\r\n	if($changes_month=="01"){$changes_month="Jan";}\r\n	if($changes_month=="02"){$changes_month="Feb";}\r\n	if($changes_month=="03"){$changes_month="Mar";}\r\n	if($changes_month=="04"){$changes_month="Apr";}\r\n	if($changes_month=="05"){$changes_month="May";}\r\n	if($changes_month=="06"){$changes_month="Jun";}\r\n	if($changes_month=="07"){$changes_month="Jul";}\r\n	if($changes_month=="08"){$changes_month="Aug";}\r\n	if($changes_month=="09"){$changes_month="Sep";}\r\n	if($changes_month=="10"){$changes_month="Oct";}\r\n	if($changes_month=="11"){$changes_month="Nov";}\r\n	if($changes_month=="12"){$changes_month="Dec";}\r\n\r\n	//convert dateandtime into a better looking string\r\n	$dateandtime = $changes_month_full." ".$changes_day.", ".$changes_year." @ ".$changes_hour.":".$changes_min.":".$changes_sec;\r\n	$dateandtime_m = $changes_month;\r\n	$dateandtime_m_full = $changes_month_full;\r\n	$dateandtime_d = $changes_day;\r\n	$dateandtime_y = $changes_year;\r\n				\r\n	echo "<li style=\\"list-style-type:none;\\"><b>[".$FETCH_CHANGELOG[''type'']."] ".$dateandtime."</b> - <span style=\\"text-align:justify;word-wrap:break-word\\">".$FETCH_CHANGELOG[''content'']."</span></li><br />";\r\n}\r\n}\r\necho "</div>";', '', '', '', '', '', '', '', 'yes', 'off', 'off');
