(1, 'padmain', 'Recently Added Anime', 'on', '/* YOU MIGHT HAVE TO SEARCH FOR THE PAD NAME (PADMAIN, PAD1 THROUGH 4, AND CHANGE IT BASED ON WHAT PAD */\r\n$pname="pages_af_atoz";\r\n$pname_special="A-Z List";\r\n$pname_uri="a-z-list";\r\n$display_user=false;\r\n$PADINFO=$properties->PAD1;\r\n$title_name="anime";\r\n/* YOU ARE GOING TO HAVE TO RENAME THE FUNCTION TO A DIF ONE SINCE YOU CANNOT */\r\n/* MULTIPLE FUNCTIONS ON THE SAME PAGE */\r\n/* ---------- DO NOT EDIT BELOW THIS LINE OR ELSE YOU WILL BREAK IT --------- */\r\n//check for meta being used\r\nif(isset($_GET[''meta''])){\r\n	//find out what meta to use\r\n	switch($_GET[''meta'']){\r\n		default:\r\n			@$credentials="";\r\n			//meta is wrong or not there, do normal page\r\n			build_anime_page(''normal'',$properties,$credentials,$pname,$pname_special,$PADINFO,$pname_uri,$title_name,$display_user,$PNAME_PUBLISHED_NAME);\r\n		break;\r\n	}\r\n} else {\r\n	//meta is not being used, do normal pname page\r\n    @$credentials="";\r\n	build_anime_page(''normal'',$properties,$credentials,$pname,$pname_special,$PADINFO,$pname_uri,$title_name,$display_user,$PNAME_PUBLISHED_NAME);\r\n}\r\n\r\nfunction build_anime_page($operation,$properties,$credentials,$pname,$pname_special,$PADINFO,$pname_uri,$title_name,$display_user,$PNAME_PUBLISHED_NAME){\r\n	switch($operation){\r\n		case ''normal'':\r\n			$GET_PNAME_ENTRIES=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_entries WHERE status=''".$PNAME_PUBLISHED_NAME."'' ORDER BY dateandtime DESC LIMIT 3") or die(''uh oh! ''.mysql_error());\r\n			if(mysql_num_rows($GET_PNAME_ENTRIES)<1){\r\n				echo "<h2>No New Entries :(</h2>";\r\n			} else {\r\n				while($FETCH_PNAME_ENTRIES=mysql_fetch_array($GET_PNAME_ENTRIES)){\r\n					$id=$FETCH_PNAME_ENTRIES[''id''];\r\n					$title=$FETCH_PNAME_ENTRIES[''title''];\r\n					$content=$FETCH_PNAME_ENTRIES[''content''];\r\n					$author=$FETCH_PNAME_ENTRIES[''author''];\r\n					$director=$FETCH_PNAME_ENTRIES[''director''];\r\n					$studio=$FETCH_PNAME_ENTRIES[''studio''];\r\n					$network=$FETCH_PNAME_ENTRIES[''network''];\r\n					$category=$FETCH_PNAME_ENTRIES[''category''];\r\n					$tags=$FETCH_PNAME_ENTRIES[''tags''];\r\n					$dateandtime=$FETCH_PNAME_ENTRIES[''dateandtime''];\r\n					$dateandtime_goingtostart=$FETCH_PNAME_ENTRIES[''dateandtime_goingtostart''];\r\n				\r\n					//set up an indicator variable\r\n					$dateandtime_indicator=$dateandtime;\r\n					\r\n					if($dateandtime=="0000-00-00 00:00:00"){\r\n						/* ART Member has not started reviewing and will start */\r\n						//make dateandtime\r\n						//0000-00-00 00:00:00\r\n						//0123456789012345678\r\n						$pname_entry_year  = substr($dateandtime_goingtostart,0,4);\r\n						$pname_entry_month = substr($dateandtime_goingtostart,5,2);\r\n						$pname_entry_day   = substr($dateandtime_goingtostart,8,2);\r\n						$pname_entry_hour  = substr($dateandtime_goingtostart,11,2);\r\n						$pname_entry_min   = substr($dateandtime_goingtostart,14,2);\r\n						$pname_entry_sec   = substr($dateandtime_goingtostart,17,2);\r\n					} else {\r\n						/* STARTED */\r\n						//make dateandtime\r\n						//0000-00-00 00:00:00\r\n						//0123456789012345678\r\n						$pname_entry_year  = substr($dateandtime,0,4);\r\n						$pname_entry_month = substr($dateandtime,5,2);\r\n						$pname_entry_day   = substr($dateandtime,8,2);\r\n						$pname_entry_hour  = substr($dateandtime,11,2);\r\n						$pname_entry_min   = substr($dateandtime,14,2);\r\n						$pname_entry_sec   = substr($dateandtime,17,2);\r\n					}\r\n					\r\n					//determine month				\r\n					if($pname_entry_month=="01"){$pname_entry_month_full="Jan";}\r\n					if($pname_entry_month=="02"){$pname_entry_month_full="Feb";}\r\n					if($pname_entry_month=="03"){$pname_entry_month_full="Mar";}\r\n					if($pname_entry_month=="04"){$pname_entry_month_full="Apr";}\r\n					if($pname_entry_month=="05"){$pname_entry_month_full="May";}\r\n	\r\n					if($pname_entry_month=="06"){$pname_entry_month_full="Jun";}\r\n					if($pname_entry_month=="07"){$pname_entry_month_full="Jul";}\r\n					if($pname_entry_month=="08"){$pname_entry_month_full="Aug";}\r\n					if($pname_entry_month=="09"){$pname_entry_month_full="Sep";}\r\n					if($pname_entry_month=="10"){$pname_entry_month_full="Oct";}\r\n					if($pname_entry_month=="11"){$pname_entry_month_full="Nov";}\r\n					if($pname_entry_month=="12"){$pname_entry_month_full="Dec";}\r\n				\r\n					//convert dateandtime into a better looking string\r\n					$dateandtime = $pname_entry_month . " " . $pname_entry_day . ", " . $pname_entry_year;\r\n					$dateandtime_m = $pname_entry_month;\r\n					$dateandtime_m_full = $pname_entry_month_full;\r\n					$dateandtime_d = $pname_entry_day;\r\n					$dateandtime_y = $pname_entry_year;\r\n				\r\n					//convert and get the director or author\r\n					if($display_user===false){\r\n						$GET_PNAME_AUTHOR_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_directors WHERE id=''{$director}''") or die(''uh oh! ''.mysql_error());\r\n						while($FETCH_PNAME_AUTHOR_INFO=mysql_fetch_array($GET_PNAME_AUTHOR_INFO)){\r\n						 $director_name=$FETCH_PNAME_AUTHOR_INFO[''name''];\r\n						 $director_shortname=$FETCH_PNAME_AUTHOR_INFO[''shortname''];\r\n						}	\r\n					} else {				\r\n						$GET_PNAME_AUTHOR_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}users WHERE id=''{$author}''") or die(''uh oh! ''.mysql_error());\r\n						while($FETCH_PNAME_AUTHOR_INFO=mysql_fetch_array($GET_PNAME_AUTHOR_INFO)){\r\n						 $uname=$FETCH_PNAME_AUTHOR_INFO[''uname''];\r\n						}\r\n					}\r\n					\r\n					//convert and get the studio\r\n					if($display_user===false){\r\n						$GET_PNAME_AUTHOR_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_studios WHERE id=''{$studio}''") or die(''uh oh! ''.mysql_error());\r\n						while($FETCH_PNAME_AUTHOR_INFO=mysql_fetch_array($GET_PNAME_AUTHOR_INFO)){\r\n						 $studio_name=$FETCH_PNAME_AUTHOR_INFO[''name''];\r\n						 $studio_shortname=$FETCH_PNAME_AUTHOR_INFO[''shortname''];\r\n						}	\r\n					} else {				\r\n						/* NONE */\r\n					}\r\n					\r\n					//convert and get the network\r\n					if($display_user===false){\r\n						$GET_PNAME_AUTHOR_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_networks WHERE id=''{$network}''") or die(''uh oh! ''.mysql_error());\r\n						while($FETCH_PNAME_AUTHOR_INFO=mysql_fetch_array($GET_PNAME_AUTHOR_INFO)){\r\n						 $network_name=$FETCH_PNAME_AUTHOR_INFO[''name''];\r\n						 $network_shortname=$FETCH_PNAME_AUTHOR_INFO[''shortname''];\r\n						}	\r\n					} else {				\r\n						/* NONE */\r\n					}\r\n				\r\n					//convert and get the category\r\n					//#,#,\r\n					if(strpos($category,",")!="-1"){\r\n						/* MULTIPLE CATEGORIES */\r\n						$category_list=explode(",",$category);\r\n						$category="";\r\n						for($icat=0; $icat<=count($category_list)-1; $icat++){\r\n							$GET_PNAME_CATEGORY_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_categories WHERE id=''{$category_list[$icat]}''") or die(''uh oh! ''.mysql_error());\r\n							while($FETCH_PNAME_CATEGORY_INFO=mysql_fetch_array($GET_PNAME_CATEGORY_INFO)){\r\n							 if($icat==count($category_list)-1){$category.="<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/category/".converter($properties,$FETCH_PNAME_CATEGORY_INFO[''name''],''url'',''to'')."\\">" . $FETCH_PNAME_CATEGORY_INFO[''name''] . "</a>";}else{$category.="<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/category/".converter($properties,$FETCH_PNAME_CATEGORY_INFO[''name''],''url'',''to'')."\\">" . $FETCH_PNAME_CATEGORY_INFO[''name''] . "</a>, ";}\r\n							}\r\n						}\r\n					} else {\r\n						$GET_PNAME_CATEGORY_INFO=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_categories WHERE id=''{$category}''") or die(''uh oh! ''.mysql_error());\r\n						while($FETCH_PNAME_CATEGORY_INFO=mysql_fetch_array($GET_PNAME_CATEGORY_INFO)){\r\n						 $category=$FETCH_PNAME_CATEGORY_INFO[''name''];\r\n						 $category="<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/category/".converter($properties,$FETCH_PNAME_CATEGORY_INFO[''name''],''url'',''to'')."\\">" . $FETCH_PNAME_CATEGORY_INFO[''name''] . "</a>";\r\n						}	\r\n					}\r\n					\r\n					//get comment for posts\r\n					$GET_COMMENTS_C=mysql_query("SELECT * FROM {$properties->DB_PREFIX}".$pname."_comments WHERE entry_id=''$id'' AND status=''Approved''");\r\n					if(mysql_num_rows($GET_COMMENTS_C)<1){\r\n						$comments_count="0 comments";\r\n					} else {\r\n						if((mysql_num_rows($GET_COMMENTS_C)>0) && (mysql_num_rows($GET_COMMENTS_C)<2)){\r\n							$comments_count=mysql_num_rows($GET_COMMENTS_C)." comment";\r\n						} else {\r\n							$comments_count=mysql_num_rows($GET_COMMENTS_C)." comments";\r\n						}\r\n					}\r\n					\r\n	\r\n					//set author to uname from db\r\n					if($display_user===false){$author_name=$name;}else{$author_name=$uname;}\r\n	\r\n					/* START BUILDING THE PNAME LIST */\r\n					echo "<div class=\\"".$title_name."-hp-container-title\\">".ucfirst($title_name)." Post</div>";\r\n					echo "<div id=\\"module-container-small\\">";\r\n						\r\n						echo "<div id=\\"module-container-small-row\\">";\r\n													\r\n							echo "<div id=\\"module-container-small-lcol\\">";\r\n								\r\n								//the dateandtime\r\n								if($dateandtime_indicator=="0000-00-00 00:00:00"){\r\n									//get todays date in pieces\r\n									$today_y=date("Y");\r\n									$today_m=date("n");\r\n									$today_d=date("j");\r\n									\r\n									//establish vars\r\n									$font_size="80%";\r\n									\r\n									//compare								\r\n									$num_of_years=$pname_entry_year - $today_y;\r\n									$num_of_months=$pname_entry_month - $today_m;\r\n									$num_of_days=$pname_entry_day - $today_d;\r\n									if($num_of_years<1){\r\n										$ending_y="s";\r\n									} else if(($num_of_years>0) && ($num_of_years<2)){\r\n										$ending_y="";\r\n									} else if($num_of_years>1){\r\n										$ending_y="s";\r\n									}\r\n									\r\n									if($num_of_months<1){\r\n										$ending_m="s";\r\n									} else if(($num_of_months>0) && ($num_of_months<2)){\r\n										$ending_m="";\r\n									} else if($num_of_months>1){\r\n										$ending_m="s";\r\n									}\r\n									\r\n									if($num_of_days<1){\r\n										$ending_d="s";\r\n									} else if(($num_of_days>0) && ($num_of_days<2)){\r\n										$ending_d="";\r\n									} else if($num_of_days>1){\r\n										$ending_d="s";\r\n									}\r\n									\r\n									//make in...\r\n									if($num_of_years < 1){\r\n										$display_inyear="";\r\n									} else {\r\n										//check to see if month and day are there\r\n										if($num_of_months > 0 && $num_of_days > 0){\r\n											$display_inyear="{$num_of_years} year{$ending_y}, ";\r\n											$font_size="80%";\r\n										} else if($num_of_months > 0 || $num_of_days > 0){\r\n											$display_inyear="{$num_of_years} year{$ending_y}, ";\r\n											$font_size="100%";\r\n										} else {\r\n											$display_inyear="{$num_of_years} year{$ending_y}";	\r\n										}\r\n									}\r\n									if($num_of_months < 1){\r\n										$display_inmonth="";\r\n									} else {\r\n										//check to see if year and day are there\r\n										if($num_of_years > 0 && $num_of_days > 0){\r\n											$display_inmonth="{$num_of_months} month{$ending_m}, ";\r\n											$font_size="80%";\r\n										} else if($num_of_years > 0 || $num_of_days > 0){\r\n											$display_inmonth="{$num_of_months} month{$ending_m}, ";\r\n											$font_size="100%";\r\n										} else {\r\n											$display_inmonth="{$num_of_months} month{$ending_m}";\r\n										}\r\n									}\r\n									if($num_of_days < 1){\r\n										$display_inday="";\r\n									} else {\r\n										//check to see if year and month are there\r\n										if($num_of_years > 0 && $num_of_months > 0){\r\n											$display_intie=" &amp; ";\r\n											$font_size="100%";\r\n											$display_inday="{$display_intie}{$num_of_days} day{$ending_d}";\r\n										} else {\r\n											$display_inday="{$num_of_days} day{$ending_d}";\r\n										}\r\n									}\r\n									if(($num_of_years == 0) && ($num_of_months == 0) && ($num_of_days == 0)){\r\n										$font_size="100%";\r\n									}\r\n									\r\n									echo "<div id=\\"module-container-small-date-goingtostart\\" style=\\"font-size: {$font_size};\\">";\r\n									//detect if date is today\r\n									if(($num_of_years == 0) && ($num_of_months == 0) && ($num_of_days == 0)){\r\n										echo "today";\r\n									} else {\r\n										echo "in {$display_inyear}{$display_inmonth}{$display_inday}";	\r\n									}\r\n									echo "</div>";\r\n								} else {\r\n									$font_size="100%";\r\n									echo "<div id=\\"module-container-small-date\\" style=\\"font-size: {$font_size};\\">";\r\n									echo $dateandtime_d." ".$dateandtime_m_full.", ".$dateandtime_y;\r\n									echo "</div>";\r\n								}\r\n								\r\n								//the author or director\r\n								echo "<div id=\\"module-container-small-author\\">";\r\n									if($display_user===false){/*CUSTOM DISPLAY*/echo "<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/directors/".$director_shortname."\\" title=\\"the director\\">" . $director_name . "</a>";}else{echo "<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/user/".$uname."\\">" . $author_name . "</a>";}\r\n								echo "</div>";\r\n								\r\n								if($display_user===false){\r\n									//the studio\r\n									echo "<div id=\\"module-container-small-studio\\">";\r\n										echo "<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/studios/".$studio_shortname."\\" title=\\"the studio\\">" . $studio_name . "</a>";\r\n									echo "</div>";\r\n									\r\n									//the network\r\n									echo "<div id=\\"module-container-small-network\\">";\r\n										echo "<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/networks/".$network_shortname."\\" title=\\"the network\\">" . $network_name . "</a>";\r\n									echo "</div>";\r\n								} else if($display_user===true) {\r\n									/* NONE */	\r\n								}\r\n															\r\n								//the comments\r\n								echo "<div id=\\"module-container-small-comments\\">";\r\n									echo "<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/permalink/".$pname_entry_year."/".$pname_entry_month."/".$pname_entry_day."/".converter($properties,$title,''url'',''to'')."#comments"."\\">".$comments_count."</a>";\r\n								echo "</div>";\r\n								\r\n								//the category\r\n								echo "<div id=\\"module-container-small-category\\">";\r\n									echo $pname_special." / " . $category;\r\n								echo "</div>";\r\n								\r\n							echo "</div>";\r\n							\r\n							echo "<div id=\\"module-container-rcol\\">";\r\n								\r\n								//the title\r\n								echo "<div id=\\"module-container-small-title\\">";\r\n	\r\n									echo "<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".$pname_uri."/permalink/".$pname_entry_year."/".$pname_entry_month."/".$pname_entry_day."/".converter($properties,$title,''url'',''to'')."\\">".$title."</a>";\r\n								echo "</div>";\r\n								\r\n								//the content\r\n								echo "<div id=\\"module-container-small-content\\">";\r\n									$content=converter($properties,$content,''article'',''to'');\r\n									$content=converter($properties,$content,''basic'',''to'');\r\n									$content=converter($properties,$content,''ncode'',''to'');\r\n									echo $content;\r\n								echo "</div>";\r\n															\r\n								//the tags\r\n								echo "<div id=\\"module-container-small-tags\\">";\r\n									//convert the tags into urls\r\n									@$tagslist=explode(",",$tags);\r\n									$tags="";\r\n									for($itgs=0; $itgs<count(@$tagslist); $itgs++){\r\n										$tags.="<a href=\\"".$properties->WEBSITE_URL.$PADINFO."/".@$pname_uri."/tag/".converter($properties,$tagslist[$itgs],''url'',''to'')."\\">".$tagslist[$itgs]."</a> ";\r\n									}\r\n							\r\n									echo "Tags: " . $tags;\r\n								echo "</div>";\r\n								\r\n							echo "</div>";\r\n							\r\n						echo "</div>";\r\n						\r\n					echo "</div>";\r\n					echo "<br />";\r\n					/* END BUILDING PNAME LIST */\r\n				}	\r\n			}\r\n		break;\r\n	}\r\n}', 'portfoliohome', 'maincontent', 0, '', 'on', 'no');
