ery("SELECT * FROM {$properties->DB_PREFIX}staff_types WHERE id=''$staff_type''") or die(''''.mysql_error());\r\n						$FETCH_STAFF_TYPE_NAME=mysql_fetch_array($GET_STAFF_TYPE_NAME);\r\n						$staff_type=$FETCH_STAFF_TYPE_NAME[''name''];\r\n						echo "<br /><i>".$staff_type."</i>";\r\n					echo "</div>";\r\n				echo "</div>";\r\n			echo "</div>";\r\n		echo "</div>";\r\n		echo "<br />";\r\n		/* END BUILDING LIST */\r\n	break;\r\n}');
